css:
@-moz-document url-prefix("https://old.reddit.com") {
/* hide so called "karma" */
.score,
.score.likes,
.arrow.down { display:none !important; }

/* center the up arrow next to topics */
.thing .midcol { padding-top:15px; }

/* remove "rank" to make the layout cleaner */
.thing .rank { display:none; }

/* simple trick to trim off the karma next to username */
#header-bottom-right { padding-left:10px; }
#header-bottom-right > * { float:left; margin:0 6px 0 0; }
#header-bottom-right .user { height:14px; overflow:hidden; }
#header-bottom-right .user a { display:block; }

}
