css:
@-moz-document domain("justin.tv") {

.dropmenu_toggled,.dropmenu_links_container,.dropmenu_col.left,.dropmenu_links_container,.dropmenu_col,#usermgmt_dropmenu,.tooltip_content_container,#header_channels_search,.form_container,#user_links p,.static_right_col,.dropmenu_alt_section,.bg_container{background:#222!important;opacity:0.98!important}

#ad_holder,.meebo-1,#footer,div#info.clear,.left_col_bottom,.footer_ad,#PopUnderChan_holder,#GrowlNotification_holder,#action_contents,#broadcast_container,[href*="itunes.apple.com"],#usermgmt_pro_upgrade,.stem,#ad_iframe,#channel_title,.speak_btn{display:none!important}

