#standard_holder{margin-left:auto!important;margin-right:auto!important}

img#lateload0.lateload{padding-left:165px!important}

.col_bg,#directory,#advanced_callout,.channel_tab{background:transparent!important}

.main,.follow_text,.videos_link{color: black!important}

body,.home {
  background: black url(http://i.imgur.com/S9ZAU.jpg) fixed 50% 0% !important
}

.site_header{ border-left: 2px #DDD !important; }
.tabs { border-bottom: 0px !important; margin-top:-38px!important }
div.wrapper { width: 1185px !important; }
#banner_custom { text-align: center !important; margin: -10px 0px 10px 0px !important; }
#related_channels { display: none !important; }
#right_col { margin: -5px 0px 0px 10px !important;}
#chat_container { width: 500px !important; height: 525px !important; }
#chat_lines { height: 450px !important; }
#standard_holder { margin-left: 5px !important; }
#stats { display: inline !important }
#stats>h3>strong:nth-child(4) { display: none !important; }
#actions { border:none!important; display: inline !important; }
#action_links { display: inline !important;  }


}
