css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {

/* Enlarge Page Content */
div#content_wrapper {
  max-width: 100% !important;
  width: 95% !important;
}
div#content {
  max-width: 100% !important;
  width: 100% !important;
}

/* Enhance header */
div#header {
  background: transparent !important;
}

ul#header_button {
  background: transparent !important;
  color: #00000 !important;
  border: none !important;
}

a#header_button_menu_title {
  text-shadow: none !important;
  color: #000000 !important;
}

/* Remove Top Wrapper */
div#zero_click_wrapper { display: none !important; }

/* Enhance links_wrapper */
div#links_wrapper {
  max-width: 100% !important;
  width: 100% !important;
}
div#links { width: 100% !important; }

/* Remove ads container */
div#ads { display: none !important; }

/* Remove Right Side Panel */
div#side { display: none !important; }

}
