css:
@-moz-document url-prefix("http://www.mangahere.com"), url-prefix("https://www.mangahere"), 
body {
background: #222; !important;
}
media="screen"
.readpage_top .title {
color: gainsboro;
font-weight: bold;
padding: 0px 0;
font-size: 12px; !important;
}
media="screen"
.readpage_top {
width: 880px;
background-color: #444;
margin: 0 auto;
}
textarea, html body textarea
   {
      font-family: Consolas, Lucida Console, Courier New, monospace !important;
   }

   body, td, input, p, a, div#person_menu_other_div,
   div.photo_note, div.photo_note_text_div, select,
   a.block
   {
      font-family: Calibri, Arial, serif !important;
   }
i.dj, i.new, i.hot {
float: left;
margin-left: 8px;
font-weight: bold;
font-size: 12px;
-webkit-text-size-adjust: none;
font-style: normal; !important;
}
i.hot {
color: #90F; !important;
}
