css:
@-moz-document url-prefix("http://dd.reddit.com/r/leagueoflegends") {

}

@-moz-document url-prefix("http://www.reddit.com/r/leagueoflegends") {

/* Front page and general */

.tabmenu li a {
    font-size: 12px !important;
    text-align: center;
}

#search input[type="text"] {
    font-size: 13px !important;
}

#header-bottom-right {
    background: url("http://a.thumbs.redditmedia.com/DhERdxd5AMqFLhHf.png") repeat scroll 0 0 transparent !important;
    width: auto !important;
    top: 129px !important;
    right: 0px !important;
    height: 22px !important;
    padding: 2px 6px 2px 0 !important;
}

#userbarToggle {
    left: -15px !important;
    background: url("http://a.thumbs.redditmedia.com/DhERdxd5AMqFLhHf.png") repeat scroll 0 0 transparent !important;
    border-radius: 0 0 0 4px !important;
    border-right: 1px solid #666666 !important;
    color: #819CB6 !important;
}

.title {
    text-shadow: 0 0 4px #CFCFCF !important;
}

.account-activity-box {
    font-size: 11px !important;
}

.side .md {
    border-radius: 4px 4px 4px 4px !important;
}

#search input[type="text"] {
    height: 19px !important;
    text-indent: 6px !important;
    border-width: 1px !important;
    border-radius: 4px 4px 4px 4px !important;
}

.side .sidecontentbox {
    border-radius: 4px 4px 4px 4px !important;
    padding-top: 4px !important;
}

/* Comment page */

.side .usertext-body {
    padding: 4px 4px 3px !important;
    border-radius: 4px 4px 4px 4px !important;
}

.tabmenu li:first-child a {
    padding-left: 16px !important;
}

.link {
    padding-top: 0px !important;
    padding-bottom: 4px !important;
}

.linkinfo {
    padding-top: 6px !important;
    padding-bottom: 2px !important;
    padding-left: 8px !important;
    border-radius: 4px !important;
}

.link .usertext .md {
    border-color: #AAAAAA !important;
}

.shortlink {
    font-size: 11px !important;
    border-color: #AAAAAA !important;
}

.shortlink input {
    font-size: 13px !important;
}

.comment {
    border-color: #D9D9D9 !important;
    /*padding-left: 8px !important;*/
    /*padding-bottom: 2px !important;*/
    padding-top: 2px !important;
    padding-right: 6px !important;
    padding-bottom: 2px !important;
    /*margin-left: 8px !important;*/
    margin-top: 2px !important;
    margin-bottom: 2px !important;
}

.comment:hover {
    border-color: #459392 !important;
}

.entry {
    padding: 4px !important;
    
    border: 1px solid transparent;
    border-bottom: 1px solid transparent !important;
}

.entry.keyHighlight {
    border-bottom: 1px dashed #257372 !important;
}

.usertext-edit textarea {
    border-width: 1px !important;
    background-color: #F0F0F0 !important;
}

/* RES fixes */

.RESSubscriptionButton {
    margin-top: 4px;
}

#RESShortcutsEditContainer {
    background-color: transparent !important;
}

#RESShortcutsRight, #RESShortcutsLeft, #RESShortcutsAdd, #RESShortcutsTrash {
    background-color: transparent !important;
}

}
