css:
@-moz-document url-prefix("https://addons.opera.com/") {



section.feedback header.h-border h3.h-section:before{content:"◎"}
 
section.feedback header.h-border{background:#f33;border-radius:14px;color:#fff;margin:0px;width:20px;height:140px}

section.feedback:hover header.h-border{margin:0px;width:auto;height:auto}

section.feedback header.h-border ~ *  ,section.feedback header.h-border h3 + nav 
 ,section.feedback header.h-border h3 + nav *
{height:0px  !important;overflow:hidden  !important;background:#fcfcfc !important}
section.feedback:hover header.h-border ~ * ,section.feedback header.h-border h3 + nav
{height:auto !important;box-shadow:inset 0 0 15px #ffc;}


section.feedback #more{background:#fcc !important;box-shadow: -1px -1px 15px #aaaaaa;}


section.feedback{

height:160px;position:fixed;right:10px;top:30px;width:20px !important;overflow:hidden;padding:20px;}
section.feedback:hover{bottom:0px;right:0px !important;padding:20px !important;width:600px !important;height:auto;background:#ffc
 !important;overflow:scroll;margin:18px 0px;
-o-transition: 0.5s ease-out;-webkit-transition: 0.5s linear;
-moz-transition: 0.5s linear;
}

section.image-viewer{position:fixed;bottom:15px;width:120px !important;overflow:hidden;padding:20px !important;height:100px;right:10px;
background:rgb(204, 15, 22);border-radius:14px;color:#fff;z-index:99;
}
section.image-viewer:hover{border-radius:16px;right:0px !important;padding:20px !important;width: 66% !important;
    height:auto;background:#233 !important;overflow:scroll;width:66%;
-o-transition: 0.5s ease;-webkit-transition: 0.5s ease;
-moz-transition: 0.5s ease;}

footer .navigation + div{position:fixed;bottom:0px;width:600px}
footer .navigation{width:600px}
form#wwwsearch:nth-child(1),.site-message,section.changelog,section.share{display:none !important;position:fixed}

}
