css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dpstream.net") {

div.raf3, div.ras3, div.rml2, div.rml3, div.rml4, div.raccueil1 , div.raccueil2 , div.rr1 i , div#pied, a.msg span, div.ras2 center font, div.rr1 div.haut, div#top iframe, #div_box {
    display: none !important;
}

#menu div {
    font-size: 0px !important;
}

#top, #affiche_fond, #degrade_fond, ul, .cadre {
    background-image: none !important;
}

a.msg {
    height: 15px !important;
    text-transform: uppercase !important;
}

body {
    background-color: #101010 !important;
    background-image: none !important;
}

div#contenu div.tggm * > div.cadre * {
    background-color: transparent !important;
    color: lightgrey !important;
    font-size: 11px !important;
}

div#contenu div.tggm * > div.cadre div {
    font-style: italic !important;
}

div#contenu div.tggm * > div.cadre u {
    padding: 0.6em !important;
}

div#contenu div.tggm * > div:not(.cadre) > b * {
    border: 50px !important;
    color: #0099FF !important;
    font-size: 15px !important;
}

div#contenu div.tggm *:not(.cadre) > div:not(.cadre) > a {
    border-width: 0px !important;
    font-size: 0px !important;
}

div#contenu div.tggm *:not(.cadre) > div:not(.ep):not(.cadre) {
    border-width: 0px !important;
    font-size: 0px !important;
}

div#contenu div.tggm > div {
    border-width: 0 !important;
    font-size: 0 !important;
}

div#contenu div.tggm > div:not(.cadre) a {
    font-size: 15px !important;
}

div#top table {
    height: 0 !important;
    margin-top: -40px !important;
}

div.rml1 div.tggm center {
    color: #ffffff !important;
    font-size: 10px !important;
}

div.rr1 {
    margin-top: 10px !important;
}

input[type=text],input[type=password] {
    background-color: #e6e6e6 !important;
    border-width: 2px !important;
    color: #000000 !important;
}

td.td-syno-3 {
    text-align: justify !important;
}

#favori_1 img[height],#favori_2 img[height],#favori_3 img[height]{
    display:none !important;
}

#favori_1 :not(img),#favori_2 :not(img),#favori_3 :not(img){
    height:9 !important;
}

#menu_favori table img{
    display:none !important;
}

}
