css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {
background: url('http://media.tumblr.com/tumblr_mdgnpvadvX1qje8rs.png') bottom right #FCF6BE no-repeat fixed !important;
}



*{
text-shadow:none !important;
-webkit-box-shadow:none !important;    
-moz-box-shadow:none !important;    
box-shadow:none !important;
}

#footer, #footer a {
color:#FFFFFF !important;
background-image:none !important;
}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(226,221,170,0.5) !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(226,221,170,0.5) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding-top: 20px;
padding-bottom: 20px;
}

h1 {color:#FFFFFF!important;}
h1:hover {color:#FFFFFF6!important;}
h2 {color:#FFFFFF!important;}
h2:hover {color:#FFFFFF!important;}
h3 {color:#FFFFFF!important;}
h3:hover {color:#FFFFFF!important;}
h4 {color:#FFFFFF!important;}
h4:hover {color:#FFFFFF!important;}
h5 {color:#FFFFFF!important;}
h5:hover {color:#FFFFFF!important;}
h6 {color:#FFFFFF!important;}
h6:hover {color:#FFFFFF!important;}

a:hover {color:#52b1e7!important}

img#content_top, img#content_bottom {
display: none;
}

#auto_pagination_loader {
background:rgba(56,161,207, 0.00)!important;
}
		
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 11px white !important;
border-right: 11px solid transparent !important;
border-left: 11px solid transparent !important; 
}

.selection_nipple:not([class="selection_nipple white"]) {
border-bottom: solid 11px #FFFFFF !important;
}	
			




#posts .post.new_post {
padding-left: 0px;
text-align: center;
text-transform: lowercase !important;
letter-spacing: 0px !important
}
	
	
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor {
background-color: transparent !important;
}
	
.avatar_frame{background-color:transparent!important;border:none!important;}
.content.is_media, 
.mask.top, 
.mask.bottom {
background: none !important; 
}

.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
border-radius: 16px !important;
}

.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
-moz-box-shadow: 1px 1px 3px #FFFFFF !important;
}

#posts .notification a:not(.avatar_frame) {
border-bottom: none !important;
}

.login.sign_up,
A[href="/forgot_password"] {
display: none !important;
}

#nav .nav_item.active {
background-color:#3e3e40;
}
	
#right_column{
color:#3e3e40;
}	

#right_column .controls_section .selected {
background:rgba(255,255,255,0.2)!important;
background-image:none!important;
border-top:1px solid rgba(0,0,0,.09)!important;
}

#right_column .controls_section .recessed {
background-color:transparent!important;
}
	
#posts .post .post_avatar {
border: 0px!important;
background-color: #222222!important;
}

#left_column #posts .notification .avatar {
border-color: #000000 !important;
background-color: #222222 !important;
}

#left_column #posts .post .permalink {
background-color:#3e3e40; 
}

#left_column #posts .post .permalink {
background: rgba(0,0,0,0.8) !important
}

#nav .nav_item.active, 
#nav .nav_item .nav_menu, 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#3e3e40 !important;
background-image:none !important;
border:0px !important
}

.notification_type_icon {
display:none !important  
}

#left_column #posts .notification {
background: rgba(0,0,0,0.8) !important;
color:#666 !important;
border-color: #333 !important;
}

#new_post_notice_container {
color:#fff !important 
}

#new_post_notice_container a {
color:#fff !important
}

#posts .notification.alt .nipple {
border-right: 6px solid #333 !important;
}

#posts .notification .nipple {
width: 0;
height: 0;
border-top: 6px solid transparent;
border-bottom: 6px solid transparent;
border-right: 6px solid #333 !important;
position: absolute;
left: -5px;
top: 10px;
}





#home_button {

    padding-right: 16px !important;
}

#home_button a {
    height: 0 !important;
    width: 0 !important;
    top: -5px !important;
    padding-left: 45px !important;
    padding-top: 50px !important;
    background: url('http://i1288.photobucket.com/albums/b493/nfjk/theme%20making/lunapic_135322107765332_17_zpscb8d711d.png') !important;
}


#home_button .tab_notice {
    left: 45px !important;
    right: auto !important;
}
}
