css:
@-moz-document domain(forums.mozfr.org)
{
/*  mise en forme jpj  */

.postprofile{
    border-right: 1px solid #CCC !important;
    border-left: none !important;
    float: left !important;
}

.postprofile dd, .postprofile dt{
    margin:0 8px 0 0 !important;
}

.postbody{
    float: right !important;
}

.postbody ul.profile-icons{
    margin-right: 30px !important;
}

#topicreview .postbody{
    width: 100% !important;
}


/*  Ajout supplémentaires */


html, body {
    background-color: #F7F7F8 !important;
}

span.corners-top {
    display:none !important;
}
.forumbg,
.forabg,
.forumbg.announcement {
    background-color: #8EBFD9 !important;
    background-image: none !important;
    border-radius:5px;
}
ul.topiclist:not(.forums):not(.topiclist) {

    height: 25px;
    text-shadow: 2px 2px 2px black;

}
    
ul.forums {
    background-image: none !important;
}
.row:nth-child(2n+1) {
    background-color: #E6F3FC !important;
    color: #2E2D40 !important;
}
.row:nth-child(2n) {
    background-color: #F2F8FB  !important;

}
li.row {
    border-bottom-color: #9ED6EF !important;
    border-top-color: #EBF2FF !important;
}

.topiclist .header {
    padding-top: 4px !important;
}
#logo > img {
    display: none;
}

h1 {
    color: rgba(42, 54, 143, 0.83);
    font-size: 300% !important;
    text-shadow: 0 0 2px #545454;
}


.navbar {
    border-radius: 5px 5px 5px 5px;
    box-shadow: 1px 1px 5px rgba(72, 108, 140, 0.2) inset, 0 0 2px rgba(66, 81, 194, 0.17);
}

.corners-bottom > span {
    background-image: none !important;
}
.corners-bottom {
    background-image: none !important;
}

.imageset.site_logo {
    background-image: none;
}

.headerbar {
    background-color: transparent !important;
    background-image: none !important;
    color:black !important;
}


#search a {
    color: black !important;
}

dl.codebox,
.content > blockquote, 
blockquote blockquote  {
    border: 0 none !important;
    box-shadow: 2px 2px 2px rgba(62, 67, 88, 0.50) !important;
}
dl.codebox {
    background-color: #FFF9F5 !important;
    margin: 5px !important;
}
.content > blockquote, 
blockquote blockquote {
    background-color: rgba(77, 140, 207, 0.20) !important;
    margin-right: 25px !important;
}
.post-icon > a,
.buttons div.post-icon {
    width: 133px !important;
}
.buttons div.reply-icon {
    width: 99px !important;
}



dl.postprofile {
    text-align: center;
}
.profile-icons {
    display: inline-block !important;
}



#postingbox.panel,
#postingbox.panel + div.panel {
    padding: 10px !important;
}

#postingbox.panel {
   background-color: #EBEBEB !important;
   border-radius: 10px 10px 0 0;
}
#postingbox.panel + div.panel {
   border-radius: 0 0 10px 10px !important;
   background-color: #D9E1E7 !important;
}

.online {
    background-position: calc(22% - 39px) 0 !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
}

    
.postprofile .has-avatar .avatar-container {
    text-align: center;
}
.postprofile .avatar {
    float: none;
    display: inline-block;
}
    
    
/* logo : Peut être supprimé s'il vous dérange */


#site-description > a {
    background-image: url("http://ikilote.net/Galeries/Autres/Divers/Geckozone_logo.png");
    background-repeat: no-repeat;
    background-size: contain;
    display: block;
    height: 100px;
    width: 250px !important;
}


/* support des flexbox */

@supports (display:flex) {

    .post {
        padding: 0 !important;
    }

    .post.online {
        background-position: calc(22% - 45px) 0 !important;
        padding-left: 0 !important;
        padding-right: 0 !important;
    }

    .post > .inner {
        display: flex;
        position: relative;
    }

    .post .postbody {
        flex: 1 1 0;
        order: 2;
        padding: 5px !important;
        width: auto !important;
    }

    .post .postprofile {
        margin: 0 !important;
        order: 1;
    }

    .post .back2top {
        order: 3;
        position: relative;
        vertical-align: bottom;
        width: 0;
        height: auto !important;
    }

    .post a.top {
        bottom: 5px;
        display: block !important;
        height: 12px !important;
        overflow: hidden !important;
        position: absolute;
        right: 5px;
        width: 12px !important;
    }

    .post .corners-bottom {
        display: none !important;
    }
}

}
