css:
/* habrahabr.ru -- Тёмный стиль [0.1] by kossmos */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("habrahabr.ru") {
	.post h1.title {
		color: #ccc !important; /* серый */
	}
	#header .search form input[type="text"] {
		background: #999 !important; /* светло-серый */
		color: #595959 !important; /* тёмно-серый */
	}
	.comments_list .comment_item .info a.avatar img,
	.sidebar_right .block .posts_list .post_item a.user_name {
		display: none !important;
	}
	.sidebar_right .block .posts_list .post_item a.post_name {
		color: #ccc !important; /* серый */
	}
	.comments_list .comment_item .message.bad1,
	.comments_list .comment_item .message.bad2,
	.comments_list .comment_item .message.bad3,
	.comments_list .comment_item .message.bad4,
	.comments_list .comment_item .message.bad5 {
		background: #595959 !important; /* тёмно-серый */
	}
	body {
		background: #595959 !important; /* тёмно-серый */
		color: #bfbfbf !important; /* светло-серый */
	}

	div.similar-posts, div.bg-bott-l {
		background: #595959 !important; /* тёмно-серый */
		color: #bfbfbf !important;	/* светло-серый */
	}

	div.page-navigation td#write {
		background: #595959 !important; /* тёмно-серый */
		border-bottom: #000 1px solid !important; /* чёрный */
	}

	div.page-navigation td, div.page-submenu ul {
		background: #595959 !important; /* тёмно-серый */
		border-bottom: #000 1px solid !important; /* чёрный */
		border-right: #000 1px solid !important; /* чёрный */
	}

	div.page-navigation td.current,
	.comments-header {
		background: #243 !important; /* тёмно-зелёный */
	}

	div, 
	div.bg-bott-l h3 {
		background-image: none !important;
	}

	.logo,
	.sidebar_right .block.new_vacanies {
		display: none !important;
	}

	.tags {
		background: #595959 !important; /* тёмно-серый */
	}

	.entry-content-only:hover {
		color: #0a8 !important; /* зелёный */
	}

	.entry-info, input#search {
		background: #111 !important; /* чёрный */
		border: 1px #000 solid !important; /* чёрный */
	}
	.sidebar_right .block {
		background-color: #595959 !important; /* тёмно-серый */
	}
	#header .main_menu a.active {
		color: #ccc !important; /* зелёный */
	}
	.content_left table.menu tr td.active a {
		background: none repeat scroll 0 0 #8c8c8c !important;
	}
	.content_left table.menu tr td.active {
		border-bottom: 1px solid #8c8c8c !important;
	}
	.content_left .submenu {
		color: #ccc !important;
		background: none #8c8c8c !important;
	}
	.content_left table.menu tr td {
		background: #595959 !important; /* тёмно-серый */
		border-right: 1px solid #666666 !important;
	}
	.comments_list .comment_item .info .voting span.plus,
	.sidebar_right .block.similar_events,
	.comments_list .comment_item .info .voting span.minus,
	.sidebar_right .banner_240x400, 
	#topline, 
	#header .main_menu .banner_special {
		display: none !important;
	}
	.post h1.title a.post_title, 
	.sidebar_right .block .blogs_list .blog_item .title a,
	.grey a, a.grey {
	   color: #ccc !important;
	}
	.post ul.tags li a {
	   color: #999 !important;
	}
	#header .userpanel {
	   position: relative !important;
	}
	.sidebar_right .block.freelansim .tasks .task a {
	   color: #BFBA3E !important;
	}
}
