css:
@-moz-document url-prefix("http://forum.indowebster.com") {
html {background-color: #000 !important}
/*body rapper */
.body_wrapper {background: #ebebeb !important;}


/* toplinks */
.toplinks {position: fixed !important; top:0 !important; padding-right:16px !important; z-index: 1 !important; background-color: #000 !important; border-bottom-right-radius: 5px !important;border-bottom-left-radius: 5px !important;-moz-border-radius-bottomleft: 5px  !important;-moz-border-radius-bottomright: 5px !important;border-top: 5px solid #C4C4C4 !important;border-bottom: 2px solid #C4C4C4 !important;border-right: 2px solid #C4C4C4 !important;border-left: 2px solid #C4C4C4 !important;}

/* navtab */
.navtabs {background-color: #000 !important;}
li [class="navtab"]{background-color: #000 !important;color: #3985a8 !important}
.announcements .announcerow {background-color: #000 !important;color: #fffff1 !important;}
.navlinks {background-color: #000 !important;filter:alpha(opacity=40) !important;opacity:0.4 !important;width: 100% !important;margin-left: auto !important;margin-right: auto !important;-moz-border-radius-topleft: 10px !important;border-top-left-radius: 10px !important;-moz-border-radius-topright: 10px !important;border-top-right-radius: 10px !important;-moz-border-radius-bottomleft: 10px !important;border-bottom-left-radius: 10px !important;-moz-border-radius-bottomright: 10px !important;border-bottom-right-radius: 10px !important;}

.navtabs ul[class="floatcontainer"] {background-color: #878787 !important;}
.navtabs ul {border-top: 0px !important;}
.navtabs li.selected li:hover a.popupctrl.active, .navtabs li.selected li a.popupctrl.active {border-color: black !important!; background-color: black; !important}
.navtabs .popupbody {border: 1px solid #000 !important ;background: #000 !important;}


/* Black forum head forumhome */
.forumhead, #wgo .blockhead, #forum_info_options .blockhead, .toolsmenu, .postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead, .newcontent_textcontrol, #pagetitle a.pagetitleinfo.textcontrol, .actionbutton_container a.pagetitleinfo.textcontrol, .thread_info h4 {
background: #000 -webkit-gradient(linear,left top,left bottom,color-stop(0,rgba(255, 255, 255, 0.35)),color-stop(0.4,rgba(255, 255, 255, 0.35)),color-stop(1,rgba(255, 255, 255, 0.05))) !important;
background: #000 -moz-linear-gradient(top,rgba(255, 255, 255, 0.35) 0%,rgba(255, 255, 255, 0.35) 40%,rgba(255, 255, 255, 0.05) 100%) !important;
background: #000 -ms-linear-gradient(top,rgba(255, 255, 255, 0.35) 0%,rgba(255, 255, 255, 0.35) 40%,rgba(255, 255, 255, 0.05) 100%) !important;
background: #000 -o-linear-gradient(top,rgba(255, 255, 255, 0.35) 0%,rgba(255, 255, 255, 0.35) 40%,rgba(255, 255, 255, 0.05) 100%) !important;}
div.blockhead {background-color: #f5f5f5 !important; }


/* black thread listing */
.blockhead, #threadlist .threadlisthead, #quickreply_title.blockhead {
background: #000 -webkit-gradient(linear,left top,left bottom,color-stop(0,rgba(255, 255, 255, 0.35)),color-stop(0.4,rgba(255, 255, 255, 0.35)),color-stop(1,rgba(255, 255, 255, 0.05))) !important;
background: #000 -moz-linear-gradient(top,rgba(255, 255, 255, 0.35) 0%,rgba(255, 255, 255, 0.35) 40%,rgba(255, 255, 255, 0.05) 100%) !important;
background: #000 -ms-linear-gradient(top,rgba(255, 255, 255, 0.35) 0%,rgba(255, 255, 255, 0.35) 40%,rgba(255, 255, 255, 0.05) 100%) !important;
background: #000 -o-linear-gradient(top,rgba(255, 255, 255, 0.35) 0%,rgba(255, 255, 255, 0.35) 40%,rgba(255, 255, 255, 0.05) 100%) !important;
}

/*sf area forum home */
historyblock #postlist li, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .threadbit .nonsticky, .threadbit .discussionrow, .threadbit.premium .sticky, .threadbit.premium .nonsticky, .threadbit.premium .replies, .threadbit.premium .threadlastpost {
background-color: #F0F0F0 !important;}

/* remove forum icon */


/*custom thread status */
a.threadstatus {width: 40px !important;}
.threadstatus {background-size: 50% !important; }


/* wgo */
.wgo_block .blockbody {background-color: #F0F0F0 !important;}

/* remove subblock info */
.options_block_container {display: none !important;}

/* forum and thread inline options */
.toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {color: white !important;}

/* darker post area */
.postbody {background-color: #c7c7c7 !important; border-right: 1px solid #999 !important; }
.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow {color: #000 !important; text-shadow: 0px 1px 0px #cccccc !important; }
span.shade {color: #000 !important;}
.postbitlegacy .postdetails, .eventbit .eventdetails {background-color: #ededed !important; border-left: 1px solid #999 !important; }
.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {border: 1px solid #999 !important;}

/* fixed black wyswyg */
.wysiwyg.forum {background-color: #f5f5f5 !important}

/* custom userinfo */
.postbitlegacy .userinfo {background: none  !important;}
.bbcode_container div.bbcode_quote {display: inline-block !important; background-color: #f5f5f5 !important;}
.username_container {text-align: center !important}
.userinfo_extra {background-image: linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);
background-image: -o-linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);
background-image: -moz-linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);
background-image: -webkit-linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);
background-image: -ms-linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);

background-image: -webkit-gradient(
	linear,
	left bottom,
	left top,
	color-stop(0.43, rgb(223,223,223)),
	color-stop(0.97, rgb(186,186,186))
) !important;border-top: 1px solid #C4C4C4 !important;border-bottom: 1px solid #C4C4C4 !important;border-right: 1px solid #C4C4C4 !important;border-left: 1px solid #C4C4C4 !important;text-align: right  !important;text-indent: 4px  !important;border-bottom-left-radius: 5px !important;border-bottom-right-radius: 5px !important;border-top-left-radius: 5px !important;border-top-right-radius: 5px !important;-moz-border-radius-topleft: 5px !important;-moz-border-radius-topright: 5px !important;-moz-border-radius-bottomleft: 5px  !important;-moz-border-radius-bottomright: 5px !important;width: 175px !important;}
.userinfo_extra dd {text-align: right !important;border-bottom: 1px dotted #c4c4c4 !important;}
div[class="username_container"] {background-image: linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);
background-image: -o-linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);
background-image: -moz-linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);
background-image: -webkit-linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);
background-image: -ms-linear-gradient(bottom, rgb(223,223,223) 43%, rgb(186,186,186) 97%);
background-image: -webkit-gradient(
linear,
left bottom,
left top,
color-stop(0.43, rgb(223,223,223)),
color-stop(0.97, rgb(186,186,186))
) !important;width: 175px !important;border-top: 1px solid #C4C4C4 !important;border-bottom: 1px solid #C4C4C4 !important;border-right: 1px solid #C4C4C4 !important;border-left: 1px solid #C4C4C4 !important;text-align: center !important;text-shadow: 0px 1px 0px #cccccc !important;border-bottom-left-radius: 5px !important;border-bottom-right-radius: 5px !important;border-top-left-radius: 5px !important;border-top-right-radius: 5px !important;-moz-border-radius-topleft: 5px !important;-moz-border-radius-topright: 5px !important;-moz-border-radius-bottomleft: 5px !important;-moz-border-radius-bottomright: 5px !important;margin-top:10px !important;padding-bottom:6px !important}


/*remove wasting space before siggy */
.postbitlegacy .after_content {clear: none !important;}

/* custom qucik reply */
.wysiwyg_block .blockrow:first-child, .wgo_block .blockbody, .forum_info .blockbody, .thread_info .blockbody {
background: whitesmoke -webkit-gradient(linear,left top,left bottom,color-stop(0,rgba(0, 0, 0, 0.1)),color-stop(0.03,rgba(255, 255, 255, 0))) !important;
background: whitesmoke -moz-linear-gradient(top,rgba(0, 0, 0, 0.1) 0%,rgba(255, 255, 255, 0) 3%) !important;
background: whitesmoke -ms-linear-gradient(top,rgba(0, 0, 0, 0.1) 0%,rgba(255, 255, 255, 0) 3%) !important;
background: whitesmoke -o-linear-gradient(top,rgba(0, 0, 0, 0.1) 0%,rgba(255, 255, 255, 0) 3%) !important;
}

/* ucp */
.blockbody {background: #f1f1f1 !important;}
.blockbody.settings_form_border {border-right: 1px solid #999 !important; border-left: 1px solid #999 !important; border-bottom: 1px solid #999 !important;}
#usercp_nav .blockbody {border: 1px solid #999 !important;}

/* profile */
#sidebar_container.member_summary h1 {text-align: center !important;}
.userprof_headers {background-color: #c2c2c2 !important;}
.userprof_content {background-color: #ebebeb !important;}
dd.userprof_module, .memberprofiletabunder {background-color: #c2c2c2 !important;}

/*hkspoiler and another style container */
dl[id="hkSpoiler"] {display: inline-block !important; position: relative !important; padding: none !important;}
div[style]:not([id], [class]) {background: #ebebeb !important; }
dd[style]:not([id]) {background: #ebebeb !important; }
div[style="overflow:auto;max-height:250px;padding:10px;background:#fff;border:1px solid #999;margin:0 20px 5px 20px"] {background: #ebebeb !important; }

/* group */
li.sgicon .maincol {background: #c2c2c2 url(http://www.indowebster.web.id/images/gradients/grey-up.png) repeat-x scroll left bottom !important;}

/* what's new */
h2.searchlisthead {background: #000 url(http://www.indowebster.web.id/images/buttons/newbtn_middle.png) repeat-x !important;}

/* search */
.formcontrols .blockrow {background: #E9E9E9 bottom repeat-x;}
.formcontrols .section {padding: 0 !important;}
#searchtypeswitcher li.selected a {background-color: #c2c2c2 !important;}
#searchtypeswitcher {border-bottom: 4px solid #c2c2c2 !important;}


/* footer */
.footer {background: #000 !important;}
.footer_time {color: #fff !important;text-align: left !important;}
span[class="time"] {color: #cf0404 !important;}
.shade{color: #fff !important;text-align: left !important;margin-left: 30px !important;}
.footer_morecopyright {color: #fff !important;text-align: left !important;margin-left: 30px !important;margin-bottom: 10px !important;}
.below_body {background-color: #000 !important;margin-left: auto !important;margin-right: auto !important;margin-top: -40px !important;margin-bottom: 10px !important;background-image: url("http://www.indojpg.com/images/818idws.png")
!important;background-image: url("http://www.indojpg.com/images/818idws.png"), linear-gradient(bottom, rgb(0,0,0) 22%, rgb(46,43,46) 61%, rgb(91,94,94) 81%)
!important;background-image: url("http://www.indojpg.com/images/818idws.png"), -o-linear-gradient(bottom, rgb(0,0,0) 22%, rgb(46,43,46) 61%, rgb(91,94,94) 81%)
!important;background-image: url("http://www.indojpg.com/images/818idws.png"), -moz-linear-gradient(bottom, rgb(0,0,0) 22%, rgb(46,43,46) 61%, rgb(91,94,94) 81%)
!important;background-image: url("http://www.indojpg.com/images/818idws.png"), -webkit-linear-gradient(bottom, rgb(0,0,0) 22%, rgb(46,43,46) 61%, rgb(91,94,94) 81%)
!important;background-image: url("http://www.indojpg.com/images/818idws.png"), -ms-linear-gradient(bottom, rgb(0,0,0) 22%, rgb(46,43,46) 61%, rgb(91,94,94) 81%)

!important;background-image: url("http://www.indojpg.com/images/818idws.png"), -webkit-gradient(
	linear,
	left bottom,
	left top,
	color-stop(0.22, rgb(0,0,0)),
	color-stop(0.61, rgb(46,43,46)),
	color-stop(0.81, rgb(91,94,94))
) !important;background-position: 100% 200% !important;background-repeat: no-repeat !important;-moz-border-radius-topright: 20px
!important;border-top-right-radius: 20px
!important;-moz-border-radius-topleft: 20px
!important;border-top-left-radius: 20px !important; }
.footer {background-color: #000 !important;}
.footer_copyright {color: #fff !important;}

/* remove ads */
div[id="adtopforum"] {display: none !important;}
div[id="IklanCPC"] {display: none !important;}
