div[id="AdsFirstPostSig"] {display: none !important;}
div[id="AdsFooterThread"] {display: none !important;}
div[id="beacon_0c1068a055"] {display: none !important;}
div[id="AdsFooterThread_dalam"] {display: none !important;}
div[id="ad_global_header2"] {display: none !important;}
div[id="ox_530da3cf1d246553d07991baa3307394"] {display: none !important;}
#Advertisement {display: none !important;}


}
