@-moz-document domain("wikipedia.org") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.catlinks,
.lang-list-button,
.mw-code,
.mw-parser-output #mp-left .mp-h2,.mw-parser-output #mp-other-lower .mp-h2,.mw-parser-output #mp-other-lower,.mw-parser-output #mp-right .mp-h2,.mw-parser-output #mp-tfp-h2,
.mw-parser-output .comportal-blocks,.mw-parser-output .comportal-section,.mw-parser-output .contentsPage--topic .contentsPage__title,.mw-parser-output .contentsPage--topic,.mw-parser-output .p-current-events-calendar,.mw-parser-output .p-current-events-news-browser,.mw-parser-output .subjectbar,.mw-parser-output .tpl-vp-sections,
.mw-rcfilters-ui-filterTagMultiselectWidget.oo-ui-widget-enabled .oo-ui-tagMultiselectWidget-handle,.mw-ui-button,
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined .oo-ui-tagItemWidget.oo-ui-widget-enabled,
.pure-button-primary-progressive,
.suggestions-results,.suggestions-special,
.thumbimage,

:focus,

[style*="background-color: #f8f9fa"],

code,
div.thumbinner,
li.gallerybox div.thumb,
pre,
table.ambox,table.ombox-notice
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
__________________________________________________________________________________________________________________________________*/

.mwe-popups-extract
{
	line-height: 2.1rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#siteNotice,

.frb-form-wrapper,
.frb-nag
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
__________________________________________________________________________________________________________________________________*/


.vector-menu-tabs {
    margin-top: -10px!important;
}

#right-navigation,
.mw-content-rtl .mw-content-ltr ul {
    margin-right: 13rem!important;
}

.mw-parser-output .current-events-navbar {
    top: 43%!important;
    right: 4.4em!important;
}

#coordinates {
    top: 14px!important;
    right: 28px!important;
}


/* Margins 
__________________________________________________________________________________________________________________________________*/


[style*="background-color:#FFFAF5"],
[style*="background-color:#FAF5FF"],
[style*="background-color:#F2FFE6"],
[style*="background-color:#F5FFFA"],
.mw-parser-output .tpl-vp-block,
.mw-parser-output .comportal-block,
.footer
{
	margin: 1.2rem!important;
}

.mw-parser-output .contentsPage--topic li,
.mw-parser-output .contentsPage--topic p {
    margin: 0.5em 15% 0.5em auto;
}


/* Paddings 
__________________________________________________________________________________________________________________________________*/


.app-badges .footer-sidebar-content,
.suggestions,

[style*="background:#F5FAFF"]
{
	padding: 1.2rem!important;
}

/* Heights 
__________________________________________________________________________________________________________________________________*/


.footer-sidebar-content
{
	max-height: none!important;
}

/* Widths 
__________________________________________________________________________________________________________________________________*/


.footer-sidebar-content
{
	max-width: none!important;
}

[style*="background-color:#F2FFE6"],
[style*="background-color:#F5FFFA"],
[style*="background-color:#FAF5FF"],
[style*="background-color:#FFFAF5"]
{
	width: 48%!important;
}

.mwe-popups.mwe-popups-type-page
{
    width: auto!important;
}

#mw-panel {
    width: 18em!important;
}

}

@-moz-document domain("wikipedia.org") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
.mw-body, #mw-head-base, #left-navigation, #mw-data-after-content, .mw-footer {
    margin-left: 21em!important;
}
	
.vector-body,
li,
p
{
	font-size: 1.5rem!important;
}

	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		
	
.mw-body, #mw-head-base, #left-navigation, #mw-data-after-content, .mw-footer {
    margin-left: 21em!important;
}
	
.vector-body,
li,
p
{
	font-size: 1.2rem!important;
}

}

}

@-moz-document domain("wikipedia.org") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
__________________________________________________________________________________________________________________________________*/

.vector-menu-tabs .selected a,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
__________________________________________________________________________________________________________________________________*/

.central-featured-lang small,
.footer-sidebar-text,
.infobox,
.mw-body,
.mw-code,
.mw-parser-output #mp-left,
.mw-parser-output #mp-topbanner,
.mw-parser-output .comportal-blocks,
.mw-parser-output .current-events-calendar,
.mw-parser-output .mp-h2,
.oo-ui-buttonElement-frameless.oo-ui-widget-enabled > .oo-ui-buttonElement-button,
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined .oo-ui-tagItemWidget.oo-ui-widget-enabled,
.other-project-tagline,
.parsoid-body,
.suggestions-result,
.tocnumber,
.wikitable,

code,
li,
p,
pre
 {
	color: #fff!important;
}

/* Khaki 
__________________________________________________________________________________________________________________________________*/

.infobox th,
.mw-parser-output .current-events-calendar th,
.wikitable > * > tr > th,
.wikitable > tr > th,

h1,
h2,
i
 {
	color: #F0E890!important;
}

/* Burlywood 
__________________________________________________________________________________________________________________________________*/

b,
h3,
h4,
span.texhtml
 {
	color: #e0b880!important;
}

/* Sandy Brown 
__________________________________________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
__________________________________________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
__________________________________________________________________________________________________________________________________*/


selector
 {
	color: #07ff24!important;
}

/* Red 
__________________________________________________________________________________________________________________________________*/


.mw-plusminus-neg
 {
	color: #ff0707!important;
}

/*
__________________________________________________________________________________________________________________________________*/

.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
__________________________________________________________________________________________________________________________________*/


::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
__________________________________________________________________________________________________________________________________*/


.svg-badge_google_play_store,
.svg-badge_ios_app_store
{
	color: #00000003!important;
	font-size: 0;
}

}

@-moz-document domain("wikipedia.org") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

#mw-head .vector-menu-dropdown h3,
#mw-page-base,

.vector-menu-tabs a,
.vector-menu-tabs li,
.vector-menu-tabs
{
	background-image: none!important;
}

div.tright
{
    background-color: #757272!important;
}

/* Background 
__________________________________________________________________________________________________________________*/


#mw-page-base,

.lang-list-button,
.mw-body,
.parsoid-body
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
__________________________________________________________________________________________________________________*/

#content .gallerybox div.thumb,

.catlinks,
.central-featured-lang :hover,
.frb,
.mw-parser-output .comportal-blocks,.mw-parser-output .contentsPage--topic .contentsPage__intro,.mw-parser-output .contentsPage--topic,.mw-parser-output .current-events-sidebar>div:not(.mw-collapsible-content),.mw-parser-output .help-box .sidebar-title,.mw-parser-output .p-current-events-calendar,.mw-parser-output .subjectbar,.mw-parser-output .tpl-vp-sections,
.mw-rcfilters-ui-filterTagMultiselectWidget.oo-ui-widget-enabled .oo-ui-tagMultiselectWidget-handle,
.mw-ui-button,.mw-ui-icon.mw-ui-icon-element.mw-ui-icon-small.mw-ui-icon-settings,
.mwe-popups-container,.mwe-popups-discreet,.mwe-popups-extract,.mwe-popups-settings-icon,.mwe-popups-thumbnail,
.navbox th,.navbox,.navbox-abovebelow,.navbox-even,.navbox-subgroup .navbox-title,.navbox-subgroup,.navbox-title,
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined .oo-ui-tagItemWidget.oo-ui-widget-enabled,
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined,
.other-project-link:hover,
.pure-button-primary-progressive,
.suggestions-results,
.thumbimage,
.wikitable,.wikitable > tr > th, .wikitable > * > tr > th,

:after,
div.thumbinner,
table.ombox,
th.navbox-group
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
__________________________________________________________________________________________________________________________________*/

/* Blue 
__________________________________________________________________________________________________________________________________*/


.mw-ui-button.mw-ui-progressive:not(:disabled)
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.mw-ui-button.mw-ui-progressive:not(:disabled)::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.mw-ui-button.mw-ui-progressive:not(:disabled):hover::before
 {
  opacity: 1!important;
}

/* Plain Black 
__________________________________________________________________________________________________________________________________*/


#mw-panel,

.footer
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
__________________________________________________________________________________________________________________________________*/

.app-badges .footer-sidebar-content,
.infobox,
.mw-parser-output #mp-left,.mw-parser-output #mp-lower,.mw-parser-output #mp-middle,.mw-parser-output #mp-right,.mw-parser-output #mp-topbanner,
