@-moz-document domain("wikipedia.org") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

img[alt="No"] {
    width: 23px;
    aspect-ratio: auto 23 / 23;
    height: 23px;
}

/* Arrows _______________________________________________________________________*/

/* Up _________________*/

selector
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

.oo-ui-indicator-down
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

selector
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

selector
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.oo-ui-indicator-down
{
	background-size: 29px!important;
	height: 30px!important;
	padding-left: 30px!important;
	width: 1px!important;
}

/* Checkmark _______________________________________________________________________________*/

/* Big Check ___________________________*/

/* Checked ___________________________*/

[alt="Yes"]
{
	background: url(https://i.ibb.co/ZMNmHwc/Mcrw-IRxnnrq.png) no-repeat!important;
	box-sizing: border-box;
}

/* Unchecked ________________________*/

selector
{
	background: url(https://i.ibb.co/thJkp9V/big-check-off.png) no-repeat!important;
	box-sizing: border-box;
}

[alt="Yes"] {
    background-size: 31px!important;
    height: 32px!important;
    padding-left: 32px!important;
    width: 1px!important;
}

/* Checked ___________________________*/

selector
{
	background: url(https://i.ibb.co/HgsTqFS/checkbox-checked.png) no-repeat!important;
	box-sizing: border-box;
