@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("board.ogame.fr") {
html {
background : url("http://img104.imageshack.us/img104/7312/carbonka0.png");
}
html body { 
width : 80% ;
margin-left: auto;
margin-right:auto;
background-color : transparent!important;
}

td.mainpage table {
width : 100% !important;
}

td.mainpage > table > tbody > tr > td > form[action="search.php"] {
position : fixed;
left : 0px;
bottom: 2px;
z-index : 10;
}
/* dÃ©place les outils modo */
form[name="modoption"]
{
position:fixed !important;
bottom:10px !important;
right:40px !important;
background : black !important;
}

/*dÃ©place le selecteur de forums*/
form[name="jumpform"]
{
position:fixed !important;
bottom:0px !important;
left:0px !important;
background : transparent !important;
width : 100%;
height : 33px;
}
form[name="jumpform"]:hover {
background : black !important;
}


/*agrandit la fenÃªtre d'Ã©criture*/
textarea[name="message"]
{
