@-moz-document domain("fukung.net") {
body {
	margin: 0px;
	background-color:#000000;
	font-size: 1em; 
	font-family: Tahoma,Verdana, Helvetica, Arial;
}

.header{
text-align: left;
padding: 5px;
margin-bottom: 15px;
}


.ad{
text-align: center;
}

.header .menu{
float: right;
font-size:12px;
padding: 5px;
}



.prettyhover { 
background: #fff;
color: red;
border: 1px solid #999;
}

.header .title{
font-weight: bolder;
font-size: 40px;
text-decoration: none;
color: #555555;
padding-left: 5px;
}

img.fukung{
background: #000000;
border: 1px solid #696969;
margin: 0px;
}

.images{
margin: 0 auto;
width:768px;
position: relative;
}


.images .image{
float: left;
width: 240px;
height: 240px;
}
	
.footer {
	font-size: 10px;; 
	text-align:center; 
	padding-top: 50px;
	width:780px;
	padding-bottom:20px;
}

a, li, div{
color: rgb(211, 200, 200);
text-decoration: none;
}

a:hover{
text-decoration: underline;
}

.clear {
	float: none;
	clear: both;
	height: 1px;
	margin: 0;
	padding: 0;
}

.main{
position: relative;
	text-align:center; 
}

.post{
font-size: 12px;
}

textarea.code{
border: 0px solid #aaa;
background: #ddd;
width: 600px;
font-size: 10px;
padding: 5px;
}

.bottommenu{
margin: 0 auto;
text-align:center;
width:768px;
font-size: 12px;
position: relative;
}



.bottomlinks {
margin: 0 auto;
width: 500px;
}



.sharelinks{
margin: 0 auto;
width: 400px;
margin-bottom: 5px;
}

.bottomlinks_like {
margin: 0 auto;
width: 300px;
text-align:center;
height: 50px;
overflow:hidden;
padding-top:10px;
}

.bottomlinks a{
text-decoration: underline;
padding-left: 10px;
padding-right: 10px;
}

.bottomcontent{
text-align: center;
}


.tags{
margin: 0 auto;
width: 500px;
margin-bottom: 10px;
font-size: 14px;
}

#taglist{
font-size: 14px;
}

#taglist a:hover{
font-size: 14px;
background: #000;
color: #696969;
