}

#tagaddlink{
color: red;
font-size: 12px;
}

.tagcloud{
margin: 0 auto;
width: 80%;
text-align: justify;
}

.paging{
padding-top: 15px;
padding-bottom: 10px;
margin: 0 auto;
width:768px;
font-size: 12px;
position: relative;
}

.pagingLeft{
text-align: left;
float: left;
}
.pagingRight{
float: right;
}

.textcontent{
margin: 0 auto;
width: 728px;
text-align: left;
}

.keys{
font-size: 010px;
text-align: center;
}


#nsfwProtect h3{
font-size: 60px;
}

.referrals{
text-align: left;
font-size: 10px;
}

.referrals ul{
list-style: none;
}

.boardcontainer{
width: 800px;
margin: 0 auto;
}

.forum{
text-align: center;
margin: 0 auto;
width: 400px;
padding-top: 10px;
padding-bottom: 10px;
}

.forumbuttons  li{
display: block;
float: left;
padding-left: 5px;
padding-right: 5px;
}

.forum .comments{
padding-top: 10px;
padding-bottom: 10px;
}

.comment{
text-align: left;
padding-left: 10px;
padding-right: 10px;
padding-bottom: 5px;
padding-top: 5px;
background: #ddd;
border: 1px solid #bbb;
margin-bottom: 5px;
}

.comment .link{
float: right;
font-size: 10px;
}
.comment .poster{
font-size: 10px;
text-align: right;
}

.forumbuttons {
margin: 0px;
}

.rating{
margin: 0 auto;
width: 130px;
}

.instruc{
font-size: 10px;
}

.bottom_menu{
font-size: 12px;
}

.bottom_menu a{
}
.bottom_menu a:hover{
text-decoration: none;
}

.bottommenu{
padding-top: 10px;
font-size: 10px;
}

.bottommenu a, .keystroke{
color: #0000FF;
}

.disclaim{
padding-top:5px;
font-size: 10px;
font-style: italic;
text-align: center;
}

.nsfwFlag{
font-size:9px;
margin: 0px;
padding: 0px;
text-align: right;
}


.urlform{
font-size: 24px;
}

#urlform{
text-align: center;
}
#urlresult{
font-size: 24px;
text-align: center;
margin-top:20px;
margin-bottom: 40px;
}

ul#gradelist
{
margin-left: 0;
padding-left: 0;
white-space: nowrap;
}

#gradelist li
{
display: inline;
list-style-type: none;
font-size: 36px;
}

#gradelist a { padding: 3px 70px; }

#gradelist a:link, #gradelist a:visited
{
color: #fff;
background-color: #036;
text-decoration: none;
}

#gradelist a:hover
{
color: #fff;
background-color: #369;
text-decoration: none;
}


#nav {
    margin: 0;
    padding: 7px 6px 0;
    line-height: 100%;
    z-index: 1000;

   
}
#nav li {
    margin: 0 5px;
    padding: 0 0 8px;
    float: left;
    position: relative;
    list-style: none;
}

#nav a {

   color: #696969;
border: 1px solid #999;
background: #000000;
    text-decoration: none;
    display: block;
    padding:  8px 20px;
    margin: 0;
}

#nav .current a, #nav li:hover > a, .button_hover {
background: #fff;
color: red;
border: 1px solid #999;
   
}

#nav ul li:hover a, #nav li:hover li a {
    background: none;
    border: none;
    color: #0000FF;
}
#nav ul a:hover {
    background: #fff !important; 
 
    color: red !important;
}

#nav ul {
    background: #ddd; 
    display: none;
    margin: 0;
    padding: 0;
    width: 150px;
    position: absolute;
    top: 35px;
    left: 0;
    border: solid 1px #b4b4b4;
     z-index: 2000;

}

#nav li:hover > ul {
    display: block;
}
#nav ul li {
    float: none;
    margin: 0;
    padding: 0;
}
#nav ul a {
    font-weight: normal;
}

#nav ul ul {
    left: 181px;
    top: -3px;
}

#nav ul li:first-child > a {
}
#nav ul li:last-child > a {
}

#nav:after {
    content: ".";
    display: block;
    clear: both;
    visibility: hidden;
    line-height: 0;
    height: 0;
}
#nav {
    display: inline-block;
}
html[xmlns] #nav {
    display: block;
}
* html #nav {
    height: 1%;
}
}
