css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iwiw.net"), domain("iwiw.hu") {
 
  #layout-table,
  #header-cell,
  #center-cell,
  #footer-cell,
  #headmenu,
  #menu,
  #content,
  #iwiw-net .inner table.datasheet,
  .searchbox,
  .cardlist {
   width: 100% !important;
   position: relative !important; }

  #headmenu .menu li,
  #headmenu .menu li a,
  #headmenu .menu li a span,
  #headmenu .menu li a span span {
   min-width:7% !important; }


 html, #iwiw-net{
  padding: 0px !important; }

 #layout-table{
  padding: 1px !important;
  margin: auto !important;
  background-color: #fff !important; }

  .banner_120,
  #header-magnum,
  div[id*="goAdverticum"],
  embed,
  div[id*="frameplace"],
  .banner_top,
  #search-right-cell,
  #right-cell-main,
  div.box.insurancebox,
  div.game_eb_box,
  #sponsors,
  #footer,
  .banner_center,
  .mm,
  .topcomsbox,
  .forumfav,
  .emote,
  .listing,
  td#left-cell div#left div[style*="background-color: rgb"] {
   display: none !important; }

  #content,
  .cardlist,
  #right-cell { 
   width: auto !important; }

  #layout-table #left-cell {
   padding:0px 0px 0px 0px !important; }

  .cardContainer .card {
   margin:5px 9px 0px 0px !important; }

  #layout-table #right-cell-main {
   padding: 0 !important; }

  #center-cell {
   padding: 0px 10px 0px 10px !important; }

  .friends #fcfForm {
   width: auto;
   margin: 0 auto 0 auto !important;}

  .friends .usersearchform {
   border-left: 5px solid #D8E4FA;
   background-color: #EBF1FC;
   margin:0 !important; }

  .suggest h4 {
   margin-top: 0px !important; }
}
