css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {

body
{
background:#333;
color:#0052FF;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:red;
}

div.l-header .selection_nipple
{
border-bottom:12px solid red;
}

#logo a
{
background-image:url(http://i.imgur.com/wi8AL.png);
}

#posts .post.new_post
{
background:url(http://i.imgur.com/SVPMh.png);
}

.new_post_label .new_post_label_icon
{
background-image:url(http://i.imgur.com/ZZOwN.png);
}

#posts .post .arrow
{
background:url(http://i.imgur.com/dmpt8.png) no-repeat -396px -247px transparent;
}

#posts .post
{
background-color:#070F11;
}

a
{
color:white;
}

#posts .post blockquote
{
border-left:solid 4px #0CF;
}

#posts .post blockquote blockquote
{
border-left:solid 4px #00A7DB;
}

#posts .post blockquote blockquote blockquote
{
border-left:solid 4px #007BDB;
}

#posts .post blockquote blockquote blockquote blockquote
{
border-left:solid 4px #004FDB;
}

#posts .post blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #003BA3;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #000DA3;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #000DA3;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #000DA3;
}

#posts .post blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote
{
border-left:solid 4px #000DA3;
}

#posts .post .permalink
{
background:url(http://i.imgur.com/fUuyc.png);
}

#posts .post.new_post .new_post_label
{
opacity:1;
color:white;
font-weight:bold;
}

#posts .post .post_question
{
color:white;
background:#059BFF;
}

#posts .post .post_question_asker
{
color:white;
}

#posts .post .post_info a
{
color:white;
}

.source_url .source_url_gradient
{
background:transparent;
}

.follower .info .name a
{
color:black;
}

#posts .post .post_question::after
{
border-top:7px solid #069bff;
}

}
