css:
/*by eridani*/
@charset "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org") {
	/* website categorization fix,"wikipedia" */
}
@-moz-document domain("wikipedia.org"),domain("wikimedia.org"),domain("wiktionary.org"),domain("wikiquote.org"),domain("wikibooks.org"),domain("wikisource.org"),domain("wikinews.org"),domain("wikiversity.org"),domain("wikimediafoundation.org"),domain("mediawiki.org"),domain("wiki.openstreetmap.org") {
	


	#firstHeading {
		width:800px !important;
		padding:1em !important;
		text-align:center !important;
		-moz-hyphens:auto;
		-webkit-hyphens:auto;
		hyphens:auto;
		color: rgba(214, 219, 223, 1) !important;
		font-size: 3em !important;
		font-weight: 100 !important;
		line-height: 0 !important;
	}
	#siteSub{
	   text-transform:uppercase !important;
	   font-size:.7em !important;
	}
	#firstHeading i:first-child {
		font-style:normal !important;
	}

	#firstHeading,h1,h2,h3,h4 {
		font-family: Helvetica,'Trebuchet MS',Arial,sans-serif !important;
		color: rgba(214, 219, 223, 1) !important;
		margin-top:30px !important;
	}
	#bodyContent {
		width:800px !important;
		text-align:justify !important;
		-moz-hyphens:auto !important;
		-webkit-hyphens:auto !important;
		hyphens:auto !important;
		font-family: Verdana,Arial,Helvetica,sans-serif !important;
		font-weight: 100 !important;
		font-size:11pt !important;
		color: rgba(214, 219, 223, 1) !important;
	}

	
	/*Table of contents*/
	.toc {
		background: rgba(50, 58, 64, .0) !important;
	}
	
	/* border around the article */
	#content {
		margin:auto !important;
		width:800px !important;
		padding:80px !important;
		padding-top:20px !important;
	}
	a{
	   color: rgba(47, 194, 239, .8) !important;
	}
	a:visited{
	   color: rgba(47, 194, 239, .5) !important;
	}
	
	/* removes the border of the menu */
	#content,.citado {
		background: rgba(50, 58, 64, .0) !important;
	}
	
	/* margins */
	.infobox,.tright,.vertical-navbox {
		margin:0 0 0.5em 2em !important;
	}
	.tleft {
		margin: 0 2em 0.5em 0 !important;
	}
	
	table,.thumbinner,.NavHead,.vertical-navbox,.infobox,.wikitable {
	   background: rgba(46, 54, 59, 1) !important;
	   border-color: rgba(255, 255, 255, .0) !important;
	   color: rgba(214, 219, 223, 1) !important;
	   font-size: 0.8em !important;
	   line-height: 1.3 !important;

	}
	td {
	   background: rgba(46, 54, 59, 1) !important;
	   border-color: rgba(255, 255, 255, .0) !important;

	}
	table,.thumbinner,.NavHead,.vertical-navbox,.infobox,.wikitable td{
	   border-width: 0 0 1px 0 !important;
	   border-color: rgba(255, 255, 255, .1) !important;
	   padding: 0.4em .5em !important;
	}
	table,.thumbinner,.NavHead,.vertical-navbox,.infobox,.wikitable td:hover{
	   border-width: 0 0 1px 0 !important;
	   border-color: rgba(255, 255, 255, .1) !important;
	   padding: 1em 1em !important;
	}
	.wikitable .table-no{
	   background: rgba(255, 144, 144, 1) !important;
	}
	.wikitable .table-yes{
	   background: rgba(144, 255, 144, 1) !important;
	}
	.headerSort,.unsortable,th,tr{
	   background: rgba(35, 41, 46, 1) !important;
	   border-width: 0 0 1px 0 !important;
	   border-color: rgba(255, 255, 255, .1) !important;
	   color: rgba(214, 219, 223, 1) !important;
	}
	
		
	/* numbering of the headings */
	body {
		counter-reset:headingOne;
	}
	#toctitle h2:before {
		content:"" !important;
		counter-reset:headingOne;
	}
	h2 {
		counter-reset:headingTwo;
	}
	h2:before {
		counter-increment:headingOne;
		content:counter(headingOne) ".";
	}
	h3 {
		counter-reset:headingThree;
	}
	h3:before {
		counter-increment:headingTwo;
		content:counter(headingOne) "." counter(headingTwo) ".";
	}
	h4 {
		counter-reset:headingFour;
	}
	h4:before {
		counter-increment:headingThree;
		content:counter(headingOne) "." counter(headingTwo) "." counter(headingThree) ".";
	}
	h5:before {
		counter-increment:headingFour;
		content: counter(headingOne) "." counter(headingTwo) "." counter(headingThree) "." counter(headingFour) ".";
	}

 



	/* removed or hidden */
	/* main elements */ body>.noprint,/*.mediawiki>div:not(.suggestions),*/
	/* footer and logo */ #footer,#p-logo,/* fundraising */ #siteNotice,#centralNotice,/* notes */ .metadata,/* sidebar */ .portal,/* ToC toggle */ span.toctoggle,/* search image */ #simpleSearch button#searchButton>img,/* selected script */ #p-variants>h4 {
		display:none !important;
	}
	/*img[src*="Bluebg_rounded"] {
		visibility: hidden !important;
	}
	*/
	/* backgrounds */
	html {
		background: rgba(50, 58, 64, 1) !important;
	}
	#p-lang>div>ul,#p-navigation>div>ul,#p-personal,#p-namespaces>ul:hover,#p-views>ul:hover,/* blog.wikimedia.org */#toc.toc {
		background:rgba(42, 49, 54, 1) !important;
		color:#FFF !important;
	}
	#mw-panel#mw-panel>.portal>h5 {
		background: rgba(250, 250, 250, .0) !important;
	}
	/* login pic */ #pt-login,/* drop-down arrows */ .portal>h5,.portal>.body,.portal,/* main tabs */ .vectorTabs,.vectorTabs>ul {
		background-image:none !important;
	}
	body,.vectorTabs>ul>li,.vectorTabs>ul>li>span {
		background: none !important;
	}


	/* list bullets */
	ul,ul li,#toc ul li,.search-types>ul>li,#pagehistory li,/*written scripts*/ #p-variants>.menu>ul>li,/* blog.wikimedia.org */ #sidebar-contents li {
		list-style:none inside none !important;
	}
	ul li {
		list-style-type:disc !important;
	}
	.searchresults>ul>li {
		list-style-type:none !important;
	}
	#toc ul {
		list-style-image:none !important;
		list-style-type: none !important;
	}
	/* header */
	#mw-head {
		display:block !important;
		position:fixed !important;
		z-index:99 !important;
		height:31px !important;
		overflow:visible !important;
		background-color:rgba(42, 49, 54, 1) ;
		color:rgba(255, 255, 255, 1);
	}
	#mw-panel {
		display:block !important;
		position:fixed !important;
		top:0 !important;
		z-index:101 !important;
		width:100% !important;
		height:1px !important;
		overflow:visible !important;
		margin:0 !important;
		padding: 0 !important;
	}
	/* search box */
	#p-search {
		position:absolute !important;
		top:0 !important;
		left:50%;
		margin:1px 0 0 !important;
		padding:3px 0 3px 56px !important;
		border-left:1px solid #111;
	}
	#searchform {
		margin:0 !important;
	}
	#simpleSearch {
		height:20px !important;
		margin:0 !important;
		padding:0 0 0 4px !important;
		border:none !important;
		-webkit-border-radius:11px !important;
		-moz-border-radius:11px !important;
		border-radius:11px !important;
	}
	#simpleSearch button#searchButton {
		min-height:20px !important;
		padding:0 !important;
		background:url("data:image/png;base64,") center no-repeat !important;
	}
	#globalWrapper #p-search {
		margin:auto 0.5em !important;
		padding:0 !important;
		border-left:none !important;
	}
	#globalWrapper #simpleSearch {
		margin-top:8px !important;
		border:1px solid !important;
	}
	#www-wikipedia-org #p-search,#www-wiktionary-org #p-search,#www-wikiquote-org #p-search,#www-wikibooks-org #p-search,#www-wikinews-org #p-search,#www-wikiversity-org #p-search {
		position:relative !important;
		top:auto !important;
		left:auto;
		margin:1em auto 1.5em !important;
		padding:0.3em !important;
		border-left: none !important;
	}
	/* search suggestions */
	html>body>.suggestions {
		right:50% !important;
		margin-right:-285px !important;
		opacity:0.94 !important;
		box-shadow:#000 2px 2px 3px;
	}
	.suggestions div /*,.suggestions span*/ {
		border-color: inherit !important;
	}
	/* Document Menu Tabs */
	.vectorTabs a span,.vectorTabs li a {
		padding:0 !important;
		height:auto !important;
		text-decoration:inherit !important;
		float:none !important;
		margin: 0 !important;
	}
	/* left menu (namespaces) | right menu (views) */
	#left-navigation,#p-namespaces,#p-views {
		position:absolute !important;
	}
	#left-navigation {
		left:5% !important;
		right:auto !important;
		top:5px !important;
		z-index:555 !important;
	}
	#p-namespaces {
		left:0 !important;
		right:auto !important;
		top:0 !important;
	}
	#p-views {
		left:auto !important;
		right:5% !important;
		top:5px !important;
		z-index:999 !important;
	}
	#p-namespaces>ul:before,#p-views>ul:before {
		content:"\2193 \2193" /*"↓↓"*/ !important;
		display:block !important;
		font-size:10px !important;
		line-height:12px !important;
	}
	#p-namespaces>ul:before {
		margin:4px 0 0 2% !important;
	}
	#p-views>ul:before {
		margin:4px 0 0 !important;
	}
	#p-namespaces>ul:hover:before {
		content:"\2192" /*"\2198"*/ /*"↘"*/ !important;
		margin:-2px 0 0 2% !important;
	}
	#p-views>ul:hover:before {
		content:"\2190" /*"\2199"*/ /*"↙"*/ !important;
		margin:-2px 0 0 90% !important;
	}
	#p-namespaces>ul:hover:before,#p-views>ul:hover:before {
		font-size:1em !important;
		line-height:inherit !important;
	}
	#p-namespaces>ul,#p-views>ul {
		height:15px !important;
		width:17px !important;
		overflow:hidden !important;
		margin:0 !important;
		padding:0 10px 2px 3px !important;
		-webkit-border-radius:5px !important;
		-moz-border-radius:5px !important;
		border-radius:5px !important;
		opacity:0.92 !important;
		cursor:default;
	}
	#p-namespaces>ul:hover,#p-views>ul:hover {
		height:auto !important;
		width:auto !important;
		border:1px solid !important;
	}
	#p-namespaces>ul>li,#p-views>ul>li {
		display:block !important;
		float:none !important;
		height:auto !important;
		margin:3px 1px 3px 3px !important;
		padding:0 3px 0 4px !important;
		border-width:0 0 0 9px !important;
		border-style:solid !important;
		border-color:#000;
		line-height:0.9em !important;
		text-decoration:none !important;
	}
	#p-namespaces>ul>li.selected,#p-views>ul>li.selected {
		border-color:#cedff2;
	}
	#p-namespaces>ul>li:hover,#p-views>ul>li:hover {
		text-decoration: underline !important;
	}
	/* variants menu,changing written script */
	#p-variants {
		position:fixed !important;
		top:40px !important;
		left:8px !important;
		z-index:600 !important;
		overflow:visible !important;
		margin:0 !important;
		cursor:default !important;
	}
	#p-variants>h5 {
		border:0 !important;
	}
	#p-variants>h5>a {
		height:10px !important;
		width:10px !important;
		background:url("data: image/png;base64,") center no-repeat !important;
	}
	/* personal portlet | table of contents */
	#bodyContent {
		z-index:auto !important;
	}
	#p-personal {
		position:absolute !important;
		right:2% !important;
		left:auto !important;
	}
	#toc.toc {
		display:block !important;
		position:fixed !important;
		right:auto !important;
		left:2% !important;
		-moz-box-sizing:content-box !important;
		-webkit-box-sizing:content-box !important;
		box-sizing:content-box !important;
	}
	#p-personal,#toc.toc {
		top:4px !important;
		z-index:9999 !important;
		height:22px !important;
		width:22px !important;
		overflow:hidden !important;
		margin:0 !important;
		padding:0 !important;
		border:0 !important;
		cursor:default !important;
	}
	#p-personal:hover,#toc.toc:hover {
		width:auto !important;
		height:auto !important;
		max-height:500px !important;
		overflow-y:auto !important;
		margin-top:2px !important;
		padding:8px !important;
		border:2px solid !important;
		-webkit-border-radius:8px !important;
		-moz-border-radius:8px !important;
		border-radius:8px !important;
		opacity:0.94 !important;
		background:rgba(42, 49, 54, 1) !important;
		box-shadow:inset #111 -1px -1px 0,inset #ccc 1px 1px 1px,-1px -1px 1px #111 !important;
	}
	li#pt-userpage,li#pt-anonuserpage,li#pt-login {
		padding:0 !important;
	}
	#p-personal li {
		display:block !important;
		float:none !important;
		margin:2px 2px 4px !important;
		padding:0 !important;
	}
	#p-personal>ul {
		margin:20px 0 0 !important;
		padding:2px 4px !important;
		text-align:left !important;
	}
	#p-personal:hover>ul {
		margin:0 !important;
	}
	#p-personal li a:hover {
		text-decoration:underline !important;
	}
	#toc td>ul {
		padding:0 !important;
	}
	.toc ul ul {
		margin-left:1.2em !important;
	}
	#p-personal>ul,#toc.toc>tbody {
		display:none !important;
	}
	#p-personal:hover>ul,#toc.toc:hover>tbody {
		display: block !important;
	}
	/* images licensed LGPL,from Nuvola iconpack */
	#toc.toc {
		background:url("data:image/png;base64,") center no-repeat !important;
		-moz-background-size:auto !important;
		background-size:auto !important;
	}
	div>#p-personal {
		background:url("data:image/png;base64,") center no-repeat !important;
		-moz-background-size:auto !important;
		background-size: auto !important;
	}
	/* panels */
	#mw-panel.collapsible-nav .portal {
		margin-top:0 !important;
		padding:0 !important;
	}
	.portal>h5 {
		font-size:0.8em !important;
	}
	.portal li a {
		font-size:1em !important;
	}
	.portal ul>li,.portal ul>li.FA {
		text-shadow: 1px 1px 1px #555 !important;
	}
	/* languages | navigation */
	.portal,.portal>.body,.portal>.body>ul {
		padding:0 !important;
	}
	#p-lang,#p-navigation {
		display:block !important;
		position:absolute !important;
		top:7px !important;
		left:50% !important;
		overflow:visible !important;
		cursor:default !important;
		color: #fff !important;
	}
	#p-lang {
		z-index:900 !important;
		margin:0 0 0 -290px !important;
	}
	#p-navigation {
		z-index:999 !important;
		margin-left:-150px !important;
	}
	#p-lang>.body,#p-navigation>.body {
		display:none !important;
		z-index:99 !important;
	}
	#p-lang>.body {
		margin-left:-50px !important;
	}
	#p-navigation>.body {
		margin-left:-20px !important;
	}
	#p-lang:hover>.body,#p-navigation:hover>.body {
		display:block !important;
	}
	#p-lang:hover>.body>ul,#p-navigation:hover>.body>ul {
		clear:both !important;
		margin:17px 0 0 !important;
		padding:2px 4px !important;
		border:1px solid;
		-webkit-border-radius:5px !important;
		-moz-border-radius:5px !important;
		border-radius:5px !important;
		opacity:0.94 !important;
	}
	#p-lang:hover>.body>ul {
		min-width:12em !important;
		max-height:500px !important;
		overflow-y:auto !important;
		-moz-column-count:3 !important;
		-moz-column-gap:16px !important;
	}
	#p-lang>h5,#p-navigation>h5 {
		display:block !important;
		float:left !important;
		padding:0 10px !important;
		color:white !important;
	}
	#p-lang>h5,#p-lang>h5>a {
		text-decoration:none !important;
		cursor:default !important;
	}
	li.interwiki-en,li.interwiki-de,li.interwiki-es,li.interwiki-fr,li.interwiki-it,li.interwiki-ru {
		font-weight:bold !important;
	}
	li.GA {
		list-style-image:url("data:image/png;base64,") !important;
	}
	li.FA {
		list-style-image:url("data: image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAANCAMAAABM3rQ0AAAAPFBMVEX//Pb/////+u76vSP6vST6wTD6wTH6wzb7xT/7xkH7y1L8023813n82YL935X94Jn+8tT+89j+9+X++OkwzhYDAAAAA3RSTlMAANUMz+KFAAAAQ0lEQVQI14XGuQGAIAAEwTtWPhVE6L9XE42daOSP/nd9G4x3B4dt9T0Bae9aFYC65JmBPC27AMWWtcUzbrJ8t+DQbj9VKAHN9/0jlQAAAABJRU5ErkJggg==") !important;
	}
	/* gallery */
	#bodyContent .gallerytext {
		font-size:0.9em !important;
		padding:0 6px 6px !important;
	}
	#bodyContent .gallerytext p {
		margin:0 !important;
		padding: 0 !important;
	}
	/* Main page - US/UK/Eng */
	body.page-Main_Page td:empty {
		border:none !important;
	}
	body.page-Main_Page table[id] table th[style] {
		margin:0 !important;
		padding: 3px !important;
	}
	ul{
	   color:rgba(214, 219, 223, 1) !important;
	}
	
	#catlinks,#mw-articlefeedback,.articleFeedback-panel,#mw-articlefeedbackv5-panel,.articleFeedbackv5,.articleFeedbackv5-buffer,.articleFeedbackv5-title-wrap {
       background:rgba(42, 49, 54, 1) !important;
    }
}
