@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

#navigator {background:#c4a667 !important ;}
#facebook, body, #welcome .messaging, #reg_features ul li, .clearfix, #poke_sidebar_header, .personal_msg span {background:#000 !important; color:#fff  !important;}

#reg_box {background:#201b11 !important; color:#ccc !important; }

h1, h2, h3, h4, h5 {color:#fff !important;}

a {color:#c4a667 !important;}

#squicklogin, #squicklogin label, #sidebar_content, 
#home_sidebar, #sidebar_signup_content, .title_header, .confirm_boxes, .confirm, #navigator .navigator_menu ul, form, .marketplace #results, .marketplace #landing, .marketplace #navigation   , .sharebox .sharebox_dialog {background:#080808 !important; color:#ccc !important;}

.sidebar_item_header {background:#2a2a2a !important;}

#error, .status, .explanation_note {background:#001417 !important; border-color:#22c3ef !important;}

#presence.full #presence_ui{background:#e5e5e5 !important; color:#fff !important;}

#navigator a {color:#fff !important;}

  #sidebar>a.go_home {background-image:url("http://farm4.static.flickr.com/3058/2564303271_fdc94200dc.jpg?v=0") !important}

.welcome h2#statement{background-image:url("http://farm4.static.flickr.com/3255/2564303305_2ea62c7835.jpg?v=0") !important}

#navigator .main_set li a.active, #navigator .main_set li a:hover {background-color: #937d50 !important;}

.status_confirm {background:#222 !important; color:#ccc !important;}



}
