css:
/**
 * Coded By Nijiko Yonskai
 * For Pandora / Pandora One Users
 * Simplified / Mobile style player.
 */
body, html { overflow: hidden !important; }

#playerBar, #mainContainer { background-color: black !important; background-position: center !important; }
#playerBar a { background: none !important; background-size: 36px 36px !important;  }
#playerBar a:hover, #playerBar .indicator a { background: url('http://i.nexua.org/icons/dark/appbar.base.png') no-repeat !important; background-size: 36px 36px !important;  }
#playerBar .info a:hover, #playerBar .cd_activator a:hover { background: none !important; }
#playerBar .thumbDownButton { 
  background: url('http://i.nexua.org/icons/dark/appbar.thumbs.down.png') no-repeat !important; 
  background-size: 36px 36px !important; 
}

#playerBar .thumbUpButton { 
  background: url('http://i.nexua.org/icons/dark/appbar.thumbs.up.png') no-repeat !important; 
  background-size: 36px 36px !important; 
}

#playerBar .playButton { 
  background: url('http://i.nexua.org/icons/dark/appbar.control.play.png') no-repeat !important; 
}

#playerBar .pauseButton { 
  background: url('http://i.nexua.org/icons/dark/appbar.control.pause.png') no-repeat !important; 
}

#playerBar .pauseButton a:hover, #playerBar .playButton a:hover {
  background-size: auto !important;
}

#playerBar .skipButton {
  background: url('http://i.nexua.org/icons/dark/appbar.control.fastforward.png') no-repeat !important; 
  background-size: 36px 36px !important; 
}

#playerBar .volumeBackground { 
  background: none !important;
}

#playerBar .volumeButton {
  background: url('http://i.nexua.org/icons/dark/appbar.sound.3.png') no-repeat !important;
  background-size: 36px 36px !important;
  width: 36px !important;
  height: 36px !important;
  top: -5px !important;
  left: -1px !important;
}

#playerBar .progress {
  font-family: Helvetica Neue !important;
 font-weight: bold;
}

#playerBar .progress .progressBar {
  background: black !important;
  border-radius: 3px !important;
}

#playerBar .searchBox, .fxCol-cont-1, .content, .contentContainer {
  background: none !important;
}

#playerBar .searchBox {
  padding: 0 !important;
}

#playerBar .searchBox input {
  color: white;
  border-bottom: 2px solid #888;
  padding: 5px;
}

#playerBar .searchBox input:focus {
  color: white;
  border-bottom-color: white;
}

.stationContent {
  position: fixed;
  left: 0;
  top: 30px;
  height: 100%;
}

.stationListHolder { height: 65% !important; }

.width-p1-noAds #adLayout {
  width: 100% !important;
}

#mainContent, .col-prop-1 {
  min-height: 0 !important;
}

.home .homeRightSide { padding-left: 0 !important; top: -20px; position: relative !important; }
.slides { width: 100% !important; height: 600px !important; margin-top: 0% !important; top: -10px; position: relative !important; }
.slide, .noart, .art { width: 100% !important; height: 100% !important; max-width: 600px !important; max-height: 600px !important; }
.treatment { width: 100% !important; height: 100% !important; }
.slide { position: relative !important; left:  390px !important; }
.slidesBackground > div { height: 0 !important; }
.slide .thumbContainer {
 margin: 0 auto !important;
 position: relative !important;
 margin-top: -68px !important;
 margin-bottom: 26px !important;
}

#adLayout {
 left:  9.5%;
}

#main {
 top: 80px !important;
}

/* Hiding */
#footer, #videoPageInfo, #detailContainer, #currentSlideArrow,
#searchPopupNavPosition, 
.contentnav, 
.top,
#trackInfo, 
#promobox, .slidesBackground img,
.platformPromo { display: none !important; }

/* Positioning */
.stationContent {
  padding-top: 60px;
}

#playerBar {
  position: fixed !important;
  bottom: 0px !important;
  z-index: 9000 !important;
  background: rgba(0, 0, 0, 0.6) !important;
  height: 88px !important;
}
