css:
@charset "utf-8";
/* CSS Document */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.baidu.com") {

	html{
		background:url(http://b.hiphotos.baidu.com/album/s%3D740%3Bq%3D90/sign=bf165995b9389b503cffe256b50e94e0/eac4b74543a98226463b68ac8a82b9014b90eb47.jpg) no-repeat center center fixed !important;
		background-size:100% auto !important;
	}
	body{
		background:transparent !important;
	}
    #s_feedback{opacity:0.5!important;}
    /*百度logo*/
    #lg{
		background-image: url(http://hiphotos.baidu.com/soinlia/pic/item/c8d19810b31bb0519deb2299367adab448ede0d9.jpg);
		background-repeat: no-repeat !important;
		background-position: center center !important;
		opacity:0.9 !important;
	}
	#lg img{
		opacity:0 !important;
	}

	/*搜索栏*/
	input[name=wd]{background:rgba(255,255,255,0.3) !important;}
	span.s_ipt_wr > input{background: none !important;}
	.btn_wr {background: transparent !important;}
	.s_btn_wr {background: none !important;}
	.s_ipt_wr{background:rgba(255,255,255,0.3) !important;}
	#search input[name="kw"],#head input[name="kw"],#fm input[name="kw"] {opacity:0.8 !important;}
	#search input[type="button"],#search input[type="submit"],#fm input[type="submit"],
	#fm input[type="button"],#head input[type="button"],#head input[type="submit"]{
		-moz-appearance:none !important;
		color:transparent !important;
		margin:0px 32px 0px -6px !important;
		
		border-radius:0px 5px 5px 0px !important;
		background:url(http://f.hiphotos.baidu.com/album/s%3D740%3Bq%3D90/sign=bf1c7bd2738da9774a2f842f806a8924/7aec54e736d12f2e4cb416814fc2d5628435689a.jpg) center center !important;
		width: 100px !important;
		height: 35px !important;
		position:relative !important;
		top:-1px !important;
		left:3px !important;
		opacity:0.6
	}
	#search input[type="button"]:hover,#search input[type="submit"]:hover,
	#head input[type="button"]:hover,#head input[type="submit"]:hover{
		box-shadow: 0px 0px 1px 0px gray !important;
	}
	#search input[type="button"]:focus,#search input[type="submit"]:focus,
	#head input[type="button"]:focus,#head input[type="submit"]:focus{
		box-shadow: 0px 0px 1px 0px gray !important;
	}
	/* 用户名下来菜单 */
    div.s-username-menu > a{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0) !important;}
	/*导航菜单*/
	div[class='mod-sc s-mod']{opacity:0.8 !important;}
	div[class='mod-sc s-mod']:hover{opacity:1.0 !important;}
	a[id='s_username_top']{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0) !important;}
	.s-pk-weather{ background:none !important;}
	#s_mod_nav{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.s-container{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
    .s-pk-addpnl{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	#s_mod_nav_titleBar{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	#container table {
		border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.8) !important;
	}
	.s-pk-mod{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	#s_nori_add_btn{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.s-nori-nav{border:none!important;}
	#s_nori_add_btn{background:none !important;}
	li.s-nori-add-btn > a{opacity: .4;}
	.pk-title{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.main clearfix{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.s-pk-mod{background:none !important;}
	/*音乐盒*/
    .s-pk-baidu-fm{opacity: .4!important;}
    /*添加栏目*/
    .pk-addwrap{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.pk-caption{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
    /*其它*/
    .s-title{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.s-c-hotlink{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.cite-form-inner{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.s-res-cite-zdy-outer{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.s-res-cite-form{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	/*添加网址*/
	.cite-name-input{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.cite-url-input{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	#rs{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	/*搜索页面*/
	.op_tieba3_title{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
	.op_tieba3_replyback{border-radius:5px 5px 5px 5px !important;
		background:rgba(255,255,255,0.4) !important;}
    #page{opacity: .6;}
    a.s_logo > img{display:none!important;}
    .s_logo{height:38px !important;width:117px!important; background:url(http://f.hiphotos.baidu.com/album/s%3D740%3Bq%3D90/sign=5fb7d4ded9f9d72a13641219e4115909/4610b912c8fcc3ce860679d29245d688d53f2008.jpg) !important;}
    table[cellspacing="0" ][cellpadding="0" ][width="30%" ][align="right"]{display:none !important;}}
