css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {


.fbx #pageHead, #blueBar #pageHead{
background: gray !important;
}

.slim #blueBar {
    background: none repeat scroll 0 0 gray !important;
}

body{
   background-image: url('http://www.zastavki.com/pictures/2560x1600/2009/3D-graphics_Gray_Texture_016438_.jpg') !important;
   background-attachment: fixed !important;
}

.uiSideNav .item, .uiSideNav .subitem {
    color: powderblue !important;
    line-height: 13px;
}

div#contentArea {
    padding-left: 18px;
    padding-right: 25px;
    width: 611px!important;
    background-image: url(http://adamdorman.com/wallpaper/gradient_1024x768.jpg)!important;
    background-attachment: fixed !important;
}

.hasRightCol #contentArea {
    float: right!important;
    margin-right: 0px;
}

DIV#rightCol{
width: 50px!important;
visibility: hidden!important;
}

body {
    font-size: 11px;
    font-family: 'lucida grande',tahoma,verdana,arial,sans-serif;
    color: royalblue !important;
    line-height: 1.28;
    text-align: left;
    direction: ltr;
}

a {
    cursor: pointer;
    color: teal !important;
    text-decoration: none;
}

#contentCol{
    background-color: #FFFFFF!important;
    opacity : 0.8 !important;
    padding-top: 15px;
}

div.mts.uiAttachmentDesc.translationEligibleUserAttachmentMessage {
    color: black !important;
}

.fcg {
    color: black !important;
}

uiStreamRedesign .uiStreamMessage .messageBody {
    color: darkslategrey!important;
    font-size: 13px;
    line-height: 1.38;
}

}
