css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
#gmi-GMFrame_BluesBar, i.dd i{display:none!important;}
.talkmessage td.f:nth-of-type(2){display:inline-block!important;}
.settings_form, .mods-column-ll, .mods-column-rr, .mods-column-hh, .split-65, #gruze-columns, #gmi-page, .gruze-main, #browse-results, .messages.f td.f:nth-of-type(2), .bubbleview{background: #E5EAE4 url(https://fc02.deviantart.net/fs70/f/2012/324/0/9/back_by_trezoid-d5lnlhw.jpg) fixed!important;}


.gtab{margin-right: 15px!important; opacity: 0.3!important;}
.gtab:hover{opacity: 0.9!important;}

.gm-chaos-edit{opacity: 0.3!important;}
.gm-chaos-edit:hover{opacity: 0.9!important;}

.gtabi-mc{opacity:1!important;}
.gr-top, .ch-ctrl, .browse-left-bar{background:rgb(218, 228, 217)!important; padding-top:3px!important;border-top-left-radius:5px!important; border-top-right-radius:5px!important; border: 1px solid #A6B2A6!important;}
.gr-box i.tri{display:none;}


.gr-top .gr{border-width:0px!important;background: transparent none!important;}
.gr-box {border-width: 0!important;border-bottom:1px solid #A6B2A6!important;border-radius: 5px;margin-bottom:32px!important;}
.gr-body, #gmi-MessageDrawPlzHolder, .cc-in, .browse-top-bar{border-bottom-left-radius: 5px!important;border-bottom-right-radius: 5px!important;background: #fff!important;}

.gr h2{color: #829181!important;}
.cc-in{margin: 5px!important;}

.mczone{background: #E5EAE4!important;}

.pager-messages .f{padding-bottom: 2px!important; padding-top:2px!important; margin-bottom: 2px!important;}
.pager-messages .f:hover{background: rgba(73, 128, 145, 0.5)!important;}
.pager-messages .icon.i4, .pager-messages .icon.i5, .ch-ctrl .l{display:none!important;}
li.a{background: #E5EAE5!important;}

.mods-column-ll #gmi-GMFrame_Gruser {margin-right: 32px!important;}

.gr-box .tt-a{margin: 4px 16px!important;}
.col-thumbs .tt-a{margin:0!important;}
.browse-top-bar{z-index:100!important;}
.browse-left-bar{border-bottom-right-radius:5px!important;}
#browse-results{padding-top:20px!important; margin-top:-5px!important;}

.browse-facet a, .oh-menu a{margin-bottom: 3px!important;}

.even, .unread{background: #fff!important;}
.odd, .opened{background: #E5EAE5!important;}

.zebra tr:first-of-type{background: #fff!important;}

.gr-headless *, .gr-headless{background: transparent none!important; border-width: 0!important;}

#oh-menu-deviant .oh-l .oh-darker{display:none!important;}
.gr1, .gr2, .gr3{display:none!important;}
.reply{margin-bottom: 5px!important;}
.avatar{margin-left: 5px!important;}

.gruze-sidebar .gr-top{border-radius: 0!important; border-width:0!important; border-bottom-width:1px!important;}

.triple td{width:20%!important;}

.journal *:not(a){color:#333}
}
