css:
@-moz-document
url-prefix("http://www.tumblr.com/dashboard"),
url-prefix("https://www.tumblr.com/dashboard"){

  /* C L E A N U P */

div.l-container
  {
    padding: 0 !important;
  }
div.l-content
  {
    padding: 52px 0 0 0 !important;
  }
div.post_avatar,
div#sidebar_footer_nav,
div#toast_float
  {
    display: none !important;
  }
div#right_column
  {
    position: absolute !important;
  }
div#left_column
  {
    width: auto !important;
    margin: 0 !important;
    padding-left: 245px !important;
  }
ol#posts
  {
    margin: 0 !important;
    padding-top: 8px !important;
    text-align: start !important;
  }

  /* G R I D */

div.l-content
  {
    text-align: center !important;
    white-space: nowrap !important;
  }
div#right_column
  {
    margin: 8px !important;
    text-align: start !important;
  }
div#left_column
  {
    white-space: normal !important;
    text-align: left !important;
  }
div#right_column,
div#left_column
  {
    float: none !important;
    display: inline-block !important;
    vertical-align: top !important;
  }
li.post_container
  {
    display: inline-block !important;
    vertical-align: top !important;
    width: 540px !important;
    margin: 8px !important;
  }

  /* N E W   P O S T   B U T T O N S */

li#new_post_buttons
  {
    width: 540px !important;
    position: absolute !important;
    top: 60px !important
  }
div#new_post
  {
    min-height: 0 !important;
    height: 60px !important;
  }
div#new_post > nav#post_buttons > a.new_post_label
  {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    height: 60px !important;
  }
div#new_post > nav#post_buttons > a.new_post_label > span
  {
    display: none !important;
  }
div#new_post > nav#post_buttons > a.new_post_label > i
  {
    margin-top: -8px !important;
  }
div.post_form_wrapper
  {
    background: white !important;
    border-radius: 6px !important;  }
ol#posts > li.post_container:nth-child(2)
  {
    margin-top: 86px !important;
  }

  /* S H A D O W S */
/*
li.post_container
  {
    box-shadow: 0 0 4px 0 #222 !important;
  }
*/

  /* T H I N   P O S T   T I T L E */

li.post_container:not(:first-child) div.post
  {
    padding: 6px 0 8px 0 !important;
  }
li.post_container:not(:first-child) div.post_header
  {
    margin-bottom: -8px  !important;
    padding: 0 10px !important;
  }
li.post_container:not(:first-child) div.post_media
  {
    margin-bottom: 0 !important;
  }
li.post_container:not(:first-child) div.post_body
  {
    padding: 0 10px !important;
  }

  /* H E A D E R */

div.l-header-container
  {
    text-align: center !important;
    min-width: 0 !important;
    max-width: none !important;
  }
div.l-header
  {
    min-width: 0 !important;
    max-width: none !important;
    width: auto !important;
  }
div.l-header > *
  {
    float: none !important;
    display: inline-block !important;
    vertical-align: middle !important;
  }
div.l-header > h1#logo
  {
    position: relative !important;
    right: 32px !important;
  }
div.l-header > div#tabs_outer_container
  {
    position: relative !important;
    left: 50px !important;
    min-width: 497px !important;
  }
div.l-header > div#tabs_outer_container > div#user_tools
  {
    float: none !important;
  }

  /* R E S P O N S I V E N E S S */

body
  {
    min-width: 572px !important;
  }
@media screen and ( max-width: 817px )
  {
    h1#logo,
    div.l-header > button.compose-button,
    div#right_column { display: none !important; }
    div#left_column { padding-left: 0 !important; }
  }
@media screen and ( max-width: 1373px )
  {
    div.l-container,
    div#left_column { width: 572px !important; }
  }
@media screen and ( min-width: 817px ) and ( max-width: 1373px )
  {
    div.l-container,
    div#left_column { width: 817px !important; }
  }
@media screen and ( min-width: 1373px ) and ( max-width: 1929px )
  {
    div.l-container { width: 1373px !important; }
  }
@media screen and ( min-width: 1929px ) and ( max-width: 2485px )
  {
    div.l-container { width: 1929px !important; }
  }
@media screen and ( min-width: 2485px ) and ( max-width: 3041px )
  {
    div.l-container { width: 2485px !important; }
  }
@media screen and ( min-width: 3041px ) and ( max-width: 3597px )
  {
    div.l-container { width: 3041px !important; }
  }
@media screen and ( min-width: 3597px ) and ( max-width: 4153px )
  {
    div.l-container { width: 3597px !important; }
  }
@media screen and ( min-width: 4153px ) and ( max-width: 4709px )
  {
    div.l-container { width: 4153px !important; }
  }
@media screen and ( min-width: 4709px ) and ( max-width: 5265px )
  {
    div.l-container { width: 4709px !important; }
  }
@media screen and ( min-width: 5265px ) and ( max-width: 5821px )
  {
    div.l-container { width: 5265px !important; }
  }
@media screen and ( min-width: 5821px )
  {
    div.l-container { width: 5821px !important; }
  }

}
