css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
domain("boards.4chan.org") {

    body {
        background-image: none !important; 
        background-color: #000 !important;
        color: #555 !important;
        font-family: "Liberation Sans" ,arial,helvetica,sans-serif !important;
    }
    .wrapper {
        background-image: none !important; 
        background-color: #000 !important;
        color: #555 !important;
        font-family: "Liberation Sans" ,arial,helvetica,sans-serif !important;
        border: #000 !important;
    }
   .desktop {
        background-image: none !important; 
        background-color: #000 !important;
        color: #888 !important;
        font-family: "Liberation Sans" ,arial,helvetica,sans-serif !important;
        border: #000 !important;
    }
    td.replyhl{
        color: #555 !important;
        background-color: #333 !important;
        border-color: #242424 !important;
    }
    span.unkfunc {
    background-color: #000;
    color: #000
    }
    .reply, hr{
        border-color: #242424 !important;
    }
    
    .reply{
        color: #555 !important;
        background-color: #242424 !important;
    }
    .subject{
        color: #a00 !important;
        background-color: #000 !important;
    }
    .replytitle, .filetitle{
        color: #8B3A3A !important;
    }
    .quote {
    color: #009900 !important;
    background: #242424 !important;
    }
    a.quotejs:active, a.quotejs:link, .doubledash{
        color: #555 !important;
    }
    
    blockquote {
        color: #9E9E9E !important;   
    }
    
    blockquote span {
        background-color: #5B5678 !important;
        color: #A4A987 !important;   
    }
      
    
    .postertrip {
        color: #567861 !important;
    }
      
    .postername, .commentpostername {
        color: #566E78 !important;
    }
    
    a, a.quotelink {
        color: #7E949E !important;
    }
    
    .postblock, .pages, th{
        background: #333 !important;
        color: #566E78 !important;
        border: #333 !important;
    }

    th font{
        color: #000 !important;
    }
    
    .pages{
        background: #242424 !important;
        color: #566E78 !important;
        border: #242424 !important;
    }
    
    .pages td{
        color: #333 !important;
    }
    
    .pages td b {
       color: #8B3A3A !important;
    }

    .unkfunc {
        color: #6E7856 !important;
    }
    
    .logo {
        color: #566E78 !important;
    }
    img, select {
        opacity: 1 !important;
    }

    img:hover {
        opacity: 1 !important;
    }
    
    .logo img {
        border: 0px !important;
    }
    
    form a img {
        opacity: 1 !important;
   }
   
    form a img:hover {
        opacity: 1 !important;
    }
    
    .recaptcha_image_cell center img, img {
         opacity: 1 !important;
         background: #333 !important;
    }
    
    .recaptcha_image_cell center img:hover {
         opacity: 1 !important;
    }
    
    #recaptcha_image {
        border: 1px solid #555 !important;
    }
      
   #header,  #navbot, #navbotr {
        color: #555 !important;
    } 
    
    .inputtext, #recaptcha_response_field {
        background-color: #333333 !important;
        color: #e6e6e6 !important;
        border-color: #555 !important;
    }
    
    input, .field {
        background-color: #333333 !important;
        border-color: #555 !important;
        color: #fff !important;
        opacity: 1 !important;
    }
    
    select#imageType{
    opacity: 1 !important;
    }

    .omittedposts {
        color: #aaa !important;
    }
    
    .abbr {
       background-color: #242424 !important;
       color: #555 !important;
    }
}
