css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.inside.com.tw") {

  /* Header */
  
  ul.dropmenu li a {
    font-size: 13px !important;
    padding: 10px 16px !important;
  }
  
  #searchform #s {
    font-size: 13px !important;
    line-height: 20px !important;
    padding: 8px 16px !important;
  }
  
  .top #searchform #searchsubmit {
    background-position: 8px 8px !important;
    height: 36px !important;
    padding: 5px !important;
  }
  
  ul#user-socials li, ul#social-icons li {
    padding:  6px 4px 4px 4px !important;
  }
  
  ul#user-socials li a, ul#social-icons li a {
    background-size: 268px 52px !important;
    width: 26px !important;
    height: 26px !important;
  }
  
  ul#user-socials li#facebook a, ul#social-icons li#facebook a {
    background-position: -32px 0 !important;
  }
  
  ul#user-socials li#twitter a, ul#social-icons li#twitter a {
    background-position: -64px 0 !important;
  }

  header h1 a {
    background-size: 204px 54px !important;
    width: 204px !important;
    height: 54px !important;
    margin: 12px 0 !important;
  }
  
  /* Content */
  
  article.page.loop, article.post.loop {
    margin-bottom: 20px !important;
    padding-bottom: 20px !important;
  }
  
  .thumb-container {
    width: 22% !important;
    margin-right: 12px !important;
  }
  
  .thumb-container .share-post {
    padding-right: 50px !important;
    background-position: 4px -292px !important;
    font-size: 14px !important;
  }
  
  .thumb-permalink {
    background-position: 2px -320px !important;
    width: 24px !important;
    height: 24px !important;
  }
  
  .post-container {
    width: 73% !important;
  }
  
  .post-container.single {
    width: 100% !important;
  }
  
  .comment-body p, .content p {
    padding: 6px 0 12px 0 !important;
  }
  
  .comment-body blockquote p, .content blockquote p {
    padding: 6px 20px 6px 30px !important;
  }
  
  #articles h2.entry-title {
    padding: 10px 0 !important;
    font-size: 20px !important;
    line-height: 26px !important;
  }
  
  #articles h2.entry-title a {
    text-decoration: none !important;
  }
  
  #articles-small h2.entry-title {
    padding: 0 !important;
    margin-bottom: 10px !important;
    line-height: 15px !important;
  }
  
  #articles-small h2.entry-title a {
    padding: 0 !important;
    margin: 0 !important;
    text-decoration: none !important;
  }
  
  .content, .excerpt {
    padding: 16px 16px 0 0 !important;
    font-size: 15px !important;
  }
  
  #articles-small h3.title {
    padding: 0 0 8px 0 !important;
    margin: 0 0 16px 0 !important;
    font-size: 16px !important;
  }
  
  #related ul li .post-container h5 a {
    font-size: 15px !important;
    line-height: 15px !important;
  }
  
  /* Sidebar */
  
  #sidebar .widget {
    margin-bottom: 20px !important;
  }
  
  ul#counters li {
    background-size: 60px 80px !important;
    margin-bottom: 0 !important;
  }
  
  ul#counters li a {
    width: 60px !important;
    padding-top: 60px !important;
  }
  
  /* Footer */
  
  .row.widfoot {
    padding-bottom: 12px !important;
  }
  
  .footer h3 {
    margin: 12px 0 !important;
    font-size: 16px !important;
  }
  
  .footer ul li {
    padding: 4px 4px 4px 14px !important;
    font-size: 14px !important;
  }
  
  .footer .textwidget div {
    text-align: left !important;
    font-size: 14px !important;
  }
}
