@-moz-document domain("laws.com") {
/* Site Settings */

.comment-content a:hover,
.mce-content-body a:hover,
.td-post-content a:hover,
.wpb_text_column a:hover,

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.content-panel .panel-header b,
.header .header-menu .main-menu > li > a,
.header .header-menu .main-menu > li:not(.has-ot-mega-menu) li > a,
.header .header-undermenu ul li a,
.sf-menu > li > a,
.shortcode-content h1,
.shortcode-content h2,
.shortcode-content h3,
.shortcode-content h4,
.shortcode-content h5,
.shortcode-content h6,
.td-category
{
	text-transform: capitalize!important;
}

.header-search-wrap .td-drop-down-search #td-header-search,
.td-pb-row [class*="td-pb-span"],
.td-post-content,
.td-post-sharing-bottom,
.td-post-sharing-top,
.wp-caption
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.td-grid-style-1.td-hover-1 .td-big-grid-post:hover .entry-thumb {
	transform: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.content-panel .panel-header b,

div b
{
	font-size: 23px!important;
}

.article-array.content-category li a,
.comment-content,
.footer-content .left,
.main-article-content .shortcode-content p,
.mce-content-body,
.post p span,
.td-header-sp-top-menu .td_data_time,
.td-post-content,
.td-weather-top-widget .td-weather-header .td-weather-city,
.td-weather-top-widget .td-weather-now,
.top-header-menu li a,
.widget_archive a,
.widget_calendar,
.widget_categories a,
.widget_display_forums a,
.widget_display_replies a,
.widget_display_stats,
.widget_display_topics a,
.widget_display_views a,
.widget_meta a,
.widget_nav_menu a,
.widget_pages a,
.widget_recent_comments a,
.widget_recent_entries a,
.widget_search input,
.widget_tag_cloud a,
.widget_text .textwidget,
.woocommerce .product-categories a,
.wpb_text_column
{
	font-size: 20px!important;
}

.article-array li a,
.article-big-block .article-content p,
.article-small-block .article-content p,
.header .header-menu .main-menu > li > a,
.header .header-menu .main-menu > li:not(.has-ot-mega-menu) li > a,
.header .header-undermenu ul li a,

.post,

body
{
	font-size: 17px!important;
}

.entry-crumbs,
.meta-info,
.sf-menu > .td-menu-item > a,
.td-category a,

body, p
{
	font-size: 15px!important;
}

.comment-content,
.mce-content-body,
.td-post-content,
.wpb_text_column
{
    line-height: 34px;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.adLink,
.article-share-bottom,
.slider-container .slider-controls,
.slider-loading,
.social-icons-float,
.widget-1.first.panel,
.widget_text,
	
iframe
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.slider-container .slider-content ul
{
	top: 0!important;
}

/* Margins
_____________________________________________________________________________________________________*/

.main-content-split .main-split-left,
.main-content-split .main-split-right,
.td-container,
	
ul li
{
	margin: 1.2rem!important;
}

.main-article-content .shortcode-content div
{
	margin: 1rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.main-content-left,
.main-content-right,
.main-content-split .main-split-left,
.main-content-split .main-split-right,
.main-nosplit .panel,
.td-container,
.very-third-menu > li ul.sub-menu,
.wrapper
{
	padding: 1.2rem!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.td-custom-image a {
    height: 25rem!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

body
{
	max-width: none!important;
}

.very-third-menu > li ul.sub-menu
{
	width: auto;
}

.slider-container .slider-content,
.td-container,
.td-pb-span12,
.td-pb-span4,
.td-pb-span8,
.td_block_big_grid,
.td_block_big_grid_2,
.td_block_big_grid_3,
.td_block_big_grid_4,
.td_block_big_grid_5,
.td_block_big_grid_6,
.td_block_big_grid_7,
.wrapper,
	
body .td-make-full
{
	width: 98%!important;
}

.very-third-menu > li > ul.sub-menu li
{
	width: 91%;
}

.main-content-left
{
	width: 76%!important;
}

.main-content-split .main-split-left,
.main-content-split .main-split-right,
.slider-loading
{
	width: 46%!important;
}

.main-content-right,
.td-pb-row .wpb_column.td-pb-span4
{
	width: 20%!important;
}

.slider-loading
{
	width: 0!important;
}

}

@-moz-document domain("laws.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.ac_results li:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.article-content,
.main-article-content .shortcode-content div,
.post p span,
.post,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.content-panel .panel-header b,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

.header-search-wrap .td-drop-down-search #td-header-search,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("laws.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.header .header-menu,
.header .header-undermenu,
.td-header-style-5 .td-top-menu-full
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.article-array li:before,
.boxed,
.breaking-news .breaking-controls,
.breaking-news .breaking-title,
.content .filter a.active,
.content-panel .panel-header b,
.content-panel .panel-header,
.header .header-menu .main-menu > li > a,
.header .header-menu .main-menu > li:not(.has-ot-mega-menu) li > a,
.header-search-wrap .td-drop-down-search .td-aj-search-results,
.main-content-right .panel h3,
.page-pager > a:hover,
.page-pager > span,
.set-image-border,
.sf-menu > .current-category-ancestor > a,
.sf-menu > .current-menu-ancestor > a,
.sf-menu > .current-menu-item > a,
.sf-menu > .sfHover > a,
.sf-menu > li > a:hover,
.shortcode-content .accordion > div > a,
.shortcode-content .spacer-break-2,
.shortcode-content .spacer-break-3,
.shortcode-content .spacer-break-4,
.shortcode-content table thead,
.slider-container .slider-content,
.slider-container .slider-controls .slider-control.active a,
.td-big-grid-post .td-module-thumb,
.td-category a,
.td-container-border:after,
.td-container-border:before,
.td-main-content:before,
.td-main-sidebar:before,
.td_block_big_grid_6,
.wp-caption,

ul li

{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.input-btn,
.td-read-more a
{	
	background: radial-gradient(circle, #031017 0%, #0a587f 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.input-btn::before,
.td-read-more a::before
{
	background: radial-gradient(circle, #0f79af 0%, #031017 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.input-btn:hover::before,
.td-read-more a:hover::before
 {
  opacity: 1!important;
}

/* Facebook 
_____________________________________________________________________________________________________*/

.td_social_facebook .td-social-box
{	
	background: radial-gradient(circle, #385499 0%, #537ce2 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.td_social_facebook .td-social-box::before
{
	background: radial-gradient(circle, #537ce2 0%, #385499 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.td_social_facebook .td-social-box:hover::before
 {
  opacity: 1!important;
}

/* Twitter
_____________________________________________________________________________________________________*/

.td_social_twitter .td-social-box
{	
	background: radial-gradient(circle, #092f3b 0%, #092f3b 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.td_social_twitter .td-social-box::before
{
	background: radial-gradient(circle, #29c5f6 0%, #092f3b 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.td_social_twitter .td-social-box:hover::before
 {
  opacity: 1!important;
}

/* YouTube
_____________________________________________________________________________________________________*/

.td_social_youtube .td-social-box
{	
	background: radial-gradient(circle, #551813 0%, #e14e42 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.td_social_youtube .td-social-box::before
{
	background: radial-gradient(circle, #e14e42 0%, #551813 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.td_social_youtube .td-social-box:hover::before
 {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

.main-content-left,
.main-content-right,
.td-container
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.ac_results,
.header-search-wrap .td-drop-down-search,
.main-content-split .main-split-left,
.main-content-split .main-split-right,
.main-nosplit .panel,
.td-header-sp-top-menu .top-header-menu .sub-menu,
.very-third-menu > li ul.sub-menu,
.wrapper
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.ac_results:hover,
.header-search-wrap .td-drop-down-search:hover,
.main-content-left:hover,
.main-content-right:hover,
.main-content-split .main-split-left:hover,
.main-content-split .main-split-right:hover,
.main-nosplit .panel:hover,
.td-container:hover,
.td-header-sp-top-menu .top-header-menu .sub-menu:hover,
.very-third-menu > li ul.sub-menu:hover,
.wrapper:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

.header .header-menu .main-menu > li.custom-class-1
{
	background: linear-gradient(to bottom, #264c84 0%, #000 49%, #264c84 100%)!important;
}

.header .header-menu .main-menu > li.custom-class-2
{
	background: linear-gradient(to bottom, #9F3819 0%, #000 49%, #9F3819 100%)!important;
}

.header .header-menu .main-menu > li.custom-class-3
{
	background: linear-gradient(to bottom, #6D8B13 0%, #000 49%, #6D8B13 100%)!important;
}

.header .header-menu .main-menu > li.custom-class-4
{
	background: linear-gradient(to bottom, #A21D1D 0%, #000 49%, #A21D1D 100%)!important;
}

.header .header-menu .main-menu > li.custom-class-5
{
	background: linear-gradient(to bottom, #338AA6 0%, #000 49%, #338AA6 100%)!important;
}

.header .header-menu .main-menu > li.custom-class-6
{
	background: linear-gradient(to bottom, #232323 0%, #000 49%, #232323 100%)!important;
}

.header .header-menu .main-menu > li.custom-class-7
{
	background: linear-gradient(to bottom, #264C84 0%, #000 49%, #264C84 100%)!important;
}

.header .header-menu .main-menu > li.custom-class-8
{
	background: linear-gradient(to bottom, #9F3819 0%, #000 49%, #9F3819 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.td-header-main-menu
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.ac_results li:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.block-title span
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("laws.com") {
/* Image Settings */

.ac_results li,
.article-side-block,
.main-article-content .shortcode-content div,
.main-content-split .main-split-left,
.main-content-split .main-split-right,
.td-block-span12,
.td-custom-image a,
	
li.cat-item
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.ac_results li:hover,
.article-side-block:hover,
.main-article-content .shortcode-content div:hover,
.main-content-split .main-split-left:hover,
.main-content-split .main-split-right:hover,
.td-block-span12:hover,
.td-custom-image a:hover,
	
li.cat-item:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

.header .header-menu ul.main-menu > li > ul.sub-menu,

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

.td-grid-style-1.td-hover-1 .td-big-grid-post:hover .td-module-thumb a:last-child:before
{
	opacity: 1!important;
}

}

@-moz-document domain("laws.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Search ___________________________________________________________________________*/

.td-icon-search:before {
    content: '';
}

.td-icon-search:before
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.td-icon-search:before
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}



}

@-moz-document domain("laws.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
