@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("your.mail.domain") {

    body * {
        font-family: Tahoma !important;
        background-color: hsla(70,60%,60%,0.3) !important;
        border-radius: 0.4em;
    }
    
    body{color: hsl(70,100%,10%)!important; }
    
    * .ctxMnu {background-color: hsla(70,60%,60%,1) !important;}
    
    a > *,a, td#tdAB, td#tdSO, span[onclick], .baseIL > #sr, .baseIL > #sr .linksVLV{
        color: hsl(310,90%,30%) !important;
    }
    
    Letter, #divBdy.bdyItmPrt, #divBdy.bdyItmPrt * {font-size: 1.05em !important;} 

    .baseIL > #sr {
        font-size: 0.8em !important;
    }
    
    .cvIL > #sr {
        height: 1em !important;
        line-height: 1em !important;
    }
    
    .baseIL > #vr {
        font-size: 8pt !important;
    }
    
    .cvIL > #vr {
        height: 21px !important;
        line-height: 21px !important;
    }
}
