css:
@-moz-document url-prefix("http://www.fixie-king.dk/forum"), url-prefix("http://fixie-king.dk/forum") {

body {background:#3D3D3D;}

#menubar {height:30px;margin-top:10px;}
#menubar a:link, #menubar a:visited {color:#EE6517;text-decoration:none;}
#menubar a strong {color:#00D6FF;}

#wrapcentre {background:#CCC;margin:0 28px;padding:10px;}

#wrapcentre {border-radius:3px;}

#pageheader { background:#3D3D3D;padding:5px 10px;border-radius:3px;}
#pageheader h2 {font-size:10px;}
#pageheader h2 a:link, #pageheader h2 a:visited {color:#EEE;} 

#logodesc, 
#datebar, 
#wrapfooter,
#menubar table td:nth-child(2) a:nth-child(1), 
#menubar table td:nth-child(2) a:nth-child(3), 
p.searchbar span:nth-child(1),
table.legend,
p.datetime,
p.moderators
{display:none; visibility:hidden;}

p.searchbar span:nth-child(2) {background:#3D3D3D;color:#e1e1e1;padding:5px 10px;border-radius:3px;}
p.searchbar span:nth-child(2) a {color:#EE6517;text-decoration:none;font-size:12px;padding:0 5px;}
p.searchbar span:nth-child(2) a:hover, #menubar a:hover {color:#EEE;}

p.forumdesc, p.topicdetails, td.nav, td.gensmall {color:#666;}

table {border-radius:3px;border:0;}
table tbody tr th {display:none;}
table tbody tr td {border-radius:3px;border:0;}
table.tablebg tr:nth-child(even) td { background:#ECECEC; }
td.row1 {background:#E2E2E2;}
td.genmed {font-size:12px}

td.profile table {display:none;}
td.profile strong {display:none;}
span.postdetails {width:200px;display:block;}

b.postauthor {font-size:12px;}

}
