@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {




*/Chat Stuff*/







.bb .fbNubButton:hover {box-shadow: inset 0 4px 8px #99ccff, 0 .5em 1em 1em #99ccff !important;}

.fbNubFlyoutTitlebar {box-shadow: inset 0 4px 8px #99ccff;padding: 0 4px !important;}








.shaded, .progress_bar_inner {box-shadow: inset 0 0 1em #99ccff;}

.UIPhotoGrid_Table .UIPhotoGrid_TableCell:hover .UIPhotoGrid_Image, #myphoto:hover, .mediaThumbWrapper:hover, .uiVideoLink:hover, .mediaThumb:hover, #presence.fbx_bar #presence_ui #presence_bar .highlight, .fbNubFlyout:hover, .hovercard .stage, #fbDockChatBuddylistNub .fbNubFlyout:hover  {box-shadow: 0 0 3em 5px #99ccff !important;}

.fbNubFlyout {box-shadow: 0 0 3em 5px #000 !important; }

.UIComposer_Attachment .UIButtond {box-shadow: none !important;}


.wsh #blueBar {
background-color: 
transparent;
min-width: 981px;
z-index: 300;
}

.wsh .slim #blueBar {
border-bottom: 0px solid 
transparent;
-webkit-box-shadow: 0 0px 0px 0px 
rgba(0, 0, 0, .52);
height: 37px;
position: relative;
}

.nsh .slim #blueBar {
background-color: 
transparent;
border-bottom: 0px solid 
transparent;
-webkit-box-shadow: 0 0px 0px 0px 
rgba(0, 0, 0, .52);
height: 37px;
min-width: 981px;
position: fixed;
z-index: 300;
}


#headNav {
background-color: 
transparent;
border: 0px solid 
transparent;
border-bottom: 0;

}





/*panel stuff */

#pageNav{
background-color: #a0d2a8;
opacity: 1 !important;

}

#jewelContainer{
background-color: #a0d2a8;
}

#blueBar{
background-color: #4c3555 !important;
}

#leftCol{
background-color: #4c3555 !important;
border-radius: 30px !important;
box-shadow: 0 1px 2px #838B83 !important;
}

#contentArea{
background-color: #a0d2a8 !important;
border-radius: 30px !important;
box-shadow: 0 1px 2px #838B83 !important;
}

#rightCol{
background-color: #e4e1e5 !important;
-webkit-transform: translateX(-15px);
border-radius: 30px !important;
box-shadow: 0 1px 2px #838B83 !important;

}


/*indv post background*/
.pagelet_home_stream{
background: #000 !important;
}

.clearfix storyContent{
background: #000 !important;
}

.ego_section{
opacity: 0 !important;
}

#pagelet_ego_pane_w{
opacity: 0 !important;
}

.topNavLink a, .blueName:hover {
color: #FFFFFF !important;
}



.home_right_column tickerOnTop{
background-color: transparent !important;
}

.rightColumnWrapper{
border: 0px !important
}

.pagelet_ego_pane_w{
background-color: transparent !important;
border: 0px !important;
}




#rightCol {
padding-top: 0px !important;
border: 0px !important;
height: 200px;

}

.fbCurrentThickline {
display: none !important;
}

#pagelet_ego_pane_w{
opacity: 0!important;
}

.navSubmenu a:hover {
color: #000 !important;
}



#profile_stream_container, #left_column, #contentArea , #leftCol, #info_tab{
background-color:none !important;
border:#fff solid 2px;
background-image:url('http://i.imgur.com/4pBX2.png') !important;
}

#rightCol, #right_column {
background-color:none !important;
border: 0px !important;
}

#contentCol {
background:none !important;
padding-top: 0px !important;
}

.presenceIndicator, .online, .mobile {
background-image: url("http://i.imgur.com/FUIQg.png") !important;
}

#pageLogo a {
background-color: transparent !important;
}
    
#pageLogo a:hover {
background-color: #000 !important;
}

a, h1, h2, h3, h4, h5 {
color: #000 !important;
}
    
a:hover {
color: #fff!important;
}

.jewelHighlight li a:hover {
background-color: #fff !important;
}


    
.fbNubButton:hover {
background: #7b7a7b !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}

.item:hover {
background: #7b7a7b !important;
color: #FFFFFF !important;
}





a.jewelButton:hover {
background-color: #FFFFFF !important;
}  

.profile-picture img {
max-width: 174px !important;
}
 
#blueBar {
border-color: #000 !important;
}

.fbNubFlyoutTitlebar {
background: -moz-linear-gradient(center top , #000, #111111) !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}
    
.fbNubFlyoutTitlebar:hover {
background: #7b7a7b !important;
}



.textInput {
color: #000 !important;

}

.uiSideNavCount:hover {
background-color: #FFFFFF !important;
color: #000 !important;
}

.count strong {
color: #FFFFFF !important;
}

.fbCurrent, .fbCurrentStory, .fbCurrentTitle {
color: #000 !important;
}

.uiHeaderSection, .uiSideHeader {
background-color: #FFFFFF !important;
border: 1px solid #000 !important;
color: #000000 !important;
}      

.submitBtn, .uiButtonConfirm {
background-color: #000 !important;
background-image: none !important;
}

.uiBoxLightblue {
background-color: #000 !important;
border: 1px solid #7b7a7b!important;
color: #FFFFFF !important;
}

.uiBoxLightblue:hover {
background-color: #7b7a7b !important;
border: 1px solid #000 !important;
color: #FFFFFF !important;
}
    
.uiUfi, .ufiItem {
background-color: #FFFFFF !important;
}

#pageNav a:hover {
color: #FFFFFF !important;
background-color: #7b7a7b !important;
}
	
#blueBar {
background: -moz-linear-gradient(center top , #000, #111111) repeat scroll 0 0 transparent !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
}

.uiLinkButton input, .like_link {
color: #000 !important;
}

.uiLinkButton input:hover, .like_link:hover {
color: #7b7a7b !important;
}

body:not(.fixedBody) 

#content {
width: 1000px !important;
margin-top: 20px !important;
}

.stat_elem, .as_link, .cmnt_like_link {
color: #000 !important;
}

.stat_elem:hover, .as_link:hover, .cmnt_like_link:hover {
color: #7b7a7b !important;
}

#blueBarHolder {
background: -moz-linear-gradient(center top , #000 #111111) repeat scroll 0 0 transparent !important;
}

.loggedout_menubar_container{
height:900px !important;
}

.loggedout_menubar{
padding-top: 100px !important;
}



