css:
/* "Google - Dark header", formerly "Google - Dark subheader"
   
   Localization issues? Leave feedback! */

@namespace url(http://www.w3.org/1999/xhtml);

/* Works on most Google addresses. */
@-moz-document regexp("https?:\/\/.*\.google\.*"){

/* Background */
.gb_Oa /* Left */,
.gb_Fb /* Center */,
.gb_Tc /* Right */,
#gbq1  /* G+ and large subheaders */{
background: #2d2d2d !important;
border-bottom: 1px white solid !important;
}

/* Remove border on services bar */
#gbx3{
border: none !important;
padding-bottom: 1px !important; /* Removes empty space */
}

/* If you are already using the style at
   http://userstyles.org/styles/78276 ,
   you are free to delete everything below. */

/* White icons
   Disable by removal or commenting out if using
   the front page exclusions. */
   
/* --> Prerequisite; Image settings */
#gbwa > DIV:first-child > A,
#gb > DIV:first-child > DIV:first-child > DIV:last-child > DIV:nth-child(3) > DIV:first-child > A > DIV:first-child,
#gb > DIV:first-child > DIV:first-child > DIV:last-child > DIV:nth-child(4) > DIV:first-child > A{
background-image: url(http://i.imgur.com/KtgpKyV.png) !Important;
background-size: 356px 144px !Important;
}
/* --> Position settings */
#gbwa > DIV:first-child > A{
background-position: -214px 0px !important;
}

#gb > DIV:first-child > DIV:first-child > DIV:last-child > DIV:nth-child(3) > DIV:first-child > A > DIV:first-child{
background-color: rgba(255, 255, 255, 0.55) !important;
background-position: -326px -121px !important;
}

#gb > DIV:first-child > DIV:first-child > DIV:last-child > DIV:nth-child(4) > DIV:first-child > A{
background-position: 0px -34px !important;
}

/* Hover color */
#gb > DIV:first-child > DIV:first-child > DIV:last-child > DIV:nth-child(3) > DIV:first-child > A > DIV:first-child:hover{
   background-color: rgb(255, 255, 255) !important;
}

/* Text color */
.gb_g,
#gbi4t{
color: white !important;
opacity: 1 !important;
}
.gb_8a{
   color: red !important;
   text-shadow: 0px 0px 6px #3d3d3d,
                0px 0px 6px #3d3d3d,
                0px 0px 6px #3d3d3d !important;
}

.gbqfqw{
border: 1px solid white !important;
border-right: none !Important;}
}
