.navbar.navbar-rt #menu .menuHeader .dropdown-menu {
	width: 94%!important;
}

.col-sm-18 {
	width: 88%!important;
}

.col-left-center {
	width: 81%!important;
}

.col-center-right {
	width: 78%!important;
}

#mainColumn,
.col-xs-5.subnav.freshPicks.inDropdownTv {
	width: 75%!important;
}

#menu .list-inline {
	width: 64%!important;
