}

.col-sm-6 {
	width: 32%!important;
}

.newsItem.col-sm-8.col-full-xs {
	width: 31%!important;
}

#listColumn {
	width: 25%!important;
}

.freshPicks .cfpItem {
	width: 18%!important;
}

.movie_list tr .left_col {
	width: 17%!important;
}

.col-sm-6.col-full-xs,
