css:
@-moz-document domain("ezsmiley.com") {
body{background-image:none !important;background-color:#880000}
#content{background-image:none !important;background-color:#660000 !important;
border-radius:20px !important;border-width:1px;border-style:solid;border-color:#000000}
#content img{display:none !important}
.postheader,#footer,#vista_toolbar ul{background-image:none !important}
#content a{color:#990000}
#leftcol{display:none}
#rightcol{width:100%;background-image:none !important;
border-radius:20px;border-width:1px;border-color:#111111 !important;border-style:solid;
background-color:#000000}
#rightcol img{display:none}
#rightcol a img{display:block !important}
#rightcol center{width:90%;margin:auto}
#albumnav{background-image:none !important;background-color:#000000}
#tdadbar{display:none !important}
#bottom{background-image:none !important;background-color:#333333}
#bottom img{display:none}
.folders img,.smileyimg img{display:block !important}
#albumnav img{display:inline !important}
#bottom a {color:#000055}
#albumnav a{color:#000077}
#container{background-color:#444444}
#vista_toolbar a:hover,#vista_toolbar a:hover span{background-image:none !important;background-color:#000055}
}
