css:
@-moz-document domain("novosti-kosmonavtiki.ru") {

/*** Главная ***/ /*** все страницы ***/
body, html
{	background-color: #001C31 !important;
	min-height: 100% !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	color: #BCBCBC !important;
	font: 12px "Verdana", "Arial","Helvetica",sans-serif !important;
}
/*** Головной блок, все страницы ***********************************************************************************************/
#header
{	background-image: none !important;
	left: 0% !important;	
	width: 100% !important;
	height: 140px !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
}
/*** логотип ***/
#logo
{	left: 2% !important;
	top: 10px !important;
	width: 207px !important;
}
/*** кнопок ***/
#topmenu
{	background-image: none !important;
	top: 75px !important;
	left: 2% !important;
	width: 462px !important;
}
/*** сдвиг кнопок влево относительно фона кнопок ***/
#topmenu div
{	margin: 0px -8px 0px -8px !important;
}
/*** в фон логотип старого дизайна, почти до 0 уменьшить номер телефона ***/
#phone
{	background: url(http://katoktau.narod.ru/img/phone.png) no-repeat !important;
	top: 79px !important;
	left: auto !important;
	right: 1% !important;
	width: 202px !important;
	height: 63px !important;
	padding: 18px 0px 0px 10px !important;
	font-family: "Arial","Helvetica",sans-serif !important;
}
/*** быстрый поиск, позиция ***/
#search-sform
{	top: 30px !important;
	left: auto !important;
	right: 2% !important;
}
/*** быстрый поиск, запрос ***/
#search-sform form input.q
{	border: 1px solid #AAAAAA !important;
}
/*** быстрый поиск, кнопка ***/
#search-sform form input.submit
{	background-color: #AAAAAA !important;
	padding: 0px 2px 0px 2px !important;
	border: none !important;
	color: #001C31 !important;
	font: bold 12px "Verdana","Arial", "Helvetica", sans-serif !important;
}
/*** войти\регистрация ***/
#login
{	left: auto !important;
	top: 5px !important;
	right: 2% !important;
}
/*** войти, логин\пароль ***/
#login-form-window, #forum-login-form-window
{	background-color: #003E63 !important;
	border: 2px solid #AAAAAA !important; 
	color: #BCBCBC !important;
}
/*** ccылки ***/
a
{	color: #ECECEC !important;
	font-family: "Verdana","Arial","Helvetica",sans-serif !important;
	text-decoration: none !important;
}
/*** ccылки с наведённым указателм ***/
a:hover
{	color: #FFA34F !important;
	text-decoration: underline !important;
}
/*** Центральный блок **********************************************************************************************************/
#center
{ 	background: #001C31 none !important;
	left: 0 !important;
	margin: 24px 5% 0px 5% !important;
	padding: 10px 0px 0px 0px !important;
	width: 90% !important;
	border: 1px solid #BCBCBC !important;
}
/*** левая колонка ***************************/
#leftcol
{	margin: 0px 0.5% 0px 0px !important;
	padding: 0px 1% 0px 2.5% !important;
	border-right: 1px solid #BCBCBC !important;
	width: 22% !important;
	line-height: 150% !important;
}
#leftcol div h3
{	color: #FFA34F !important;
	font-family: "Verdana", "Arial", "Helvetica", sans-serif !important;
}
#leftcol div h3 a
{	color: #ECECEC !important;
}
#leftcol div h3 a:hover
{	color: #FFA34F !important;
}
#leftcol div.left_news p a
{	color: #ECECEC !important;
}
#leftcol div.left_news p a:hover
{	color: #FFA34F !important;
}
/*** правая колонка **************************/
#wa
{	margin: 15px 0px 0px 0px !important;
	padding: 0px 0px 50px 2% !important;
	width: 66% !important;
	border-left: 1px solid #BCBCBC !important;
}
#wa h1
{	color: #FFA34F !important;
	font-family: "Verdana", "Arial", "Helvetica", sans-serif !important;
	font-weight: normal !important;
}
/*** Блок время, фото, ФИО, должность ***/
.card
{	margin: 0px 0% 20px 0% !important;
	width: 33% !important;
	min-width: 122px !important;
}
/*** абзац ФИО ***/
.card p.name
{	width: 122px !important;
}
/*** текст Командир МКС, ... ***/
.card p.name span
{	color: #FFA34F !important;
	font-family: "Arial", "Helvetica", sans-serif !important;
}
/*** Нижний блок ***************************************************************************************************************/
#footer1, #footer2
{ 	background: #001C31 none !important;
	left: 5% !important;
	width: 90% !important;
	height: 0px !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	border-left: 1px solid #BCBCBC !important;
	border-right: 1px solid #BCBCBC !important;
}
#footer2
{	height: 80px !important;	
	border-bottom: 1px solid #BCBCBC !important;
}
#botmenustr
{	margin: 0px 0% 0px 0% !important;
	padding: 25px 0% 0px 2% !important;
	width: 90% !important;
}
#botmenustr a
{	padding:  0px 1% 0px 1% !important;
}
#botmenustr a.selected
{	color: Gray !important;
	text-decoration: underline !important;
}
#copyright
{	margin: 0px 0px 0px 0px !important;
	padding: 25px 0px 0px 1% !important;
}
#counters
{	margin: 0px 0px 0px 0px !important;
	padding: 0px 1% 5px 0% !important;
}
#bottomline
{	background: none !important;
	height: 30px !important;
	margin: 0px 0px 0px 0px !important;
}

/*** Журнал ********************************************************************************************************************/
/*** правая колонка **************************/
#wa div.newnomer
{	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 50px 2% !important;
	line-height: 150% !important;
}
#wa div.newnomer h1
{	color: #FFA34F !important;
	font-family: "Verdana", "Arial", "Helvetica", sans-serif !important;
	font-weight: normal !important;
}
#wa div.newnomer h2 a
{	font-weight: bold !important
}
#wa div.newnomer h2
{	color: #FFA34F !important;
	font-size : 14pt !important;
	font-weight: normal !important;
}
#wa div.newnomer div.pred
{	margin: 0px 1.5% 0px 1.5% !important;
}
#wa div.newnomer div.pred p
{	margin: 5px 0px 0px 0px !important;
}
#wa div.newnomer div.pred a
{	font-family: "Arial", "Helvetica", sans-serif !important;
}

/*** Журнал, архив номеров *****************************************************************************************************/
/*** правая колонка **************************/
#wa div.nomera
{	margin: 0px 0px 0px 0px !important;
	padding: 25px 0px 50px 2% !important;
}
#wa div.nomera div
{	margin: 0px 1.5% 0px 1.5% !important;
	height: 209px !important;
}
#wa div.nomera div p
{	margin: 5px 0px 0px 0px !important;
	text-align: center !important;
}
#wa div.nomera div a
{	font-family: "Arial", "Helvetica", sans-serif !important;
	font-size: 10pt !important;
}

/*** Журнал, номер *************************************************************************************************************/
/*** левая колонка ***************************/
#magindex div p
{	color: #ECECEC !important;
}
#magindex div p:hover
{	color: #FFA34F !important;
	text-decoration: underline !important;
}
#magindex div div p
{	color: #BCBCBC !important;
	margin: 3px 0px 7px 10% !important;
}
#magindex div div p:hover
{	color: #BCBCBC !important;
	text-decoration: none !important;
}
/*** правая колонка **************************/
#wa div.nomer
{	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 50px 2% !important;
}
/*** Журнал, статья **************************/
#wa img
{	vertical-align: text-bottom !important;
}

/*** Новости *******************************************************************************************************************/
/*** левая колонка ***************************/
#leftcol div.news-filter form
{	width: 145px !important;
}
/*** правая колонка **************************/
.news-list
{	background-color: #003E63 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	border: 2px solid #006699 !important;
	width: 85% !important;
}
p.news-item
{	margin: 0px 0px 0px 0px !important;
	padding: 5px 1.5% 0px 1.5% !important;
	border-bottom: 1px solid #001C31 !important;
	line-height: 150% !important;
}
span.news-date-time
{	color: #FFA34F !important;
	font-size : 11px !important;
}
/*** Главная форума ************************************************************************************************************/
#wa2
{	margin: 0px 0px 0px 0px !important;
	padding: 0px 1% 0px 1% !important;
}
.forum-menu-box
{	background: #001C31 url(http://novosti-kosmonavtiki.ru/bitrix/components/bitrix/forum.interface/show_file.php?fid=20389) top no-repeat !important;
	margin: 0px 0px 20px 0px !important;
	padding: 50px 0px 0px 0px !important;
	border: none !important;
	text-align: center !important;
}
.forum-menu-item
{	margin: 0px 2% 0px 2% !important;
	border-right: none !important;
	font-size: 11px !important;
}
.forum-header-box
{	background: #003E63 url(http://88.210.62.157/phpBB2/templates/subSilver/images/cellpic3_nk.gif) repeat !important;
	border: 2px solid #006699 !important;
	border-bottom: 1px solid #001C31 !important;
	height: 28px !important;
	color: #FFA34F !important;
	font: bold 11px "Verdana", "Arial", "Helvetica", sans-serif !important;
}
.forum-header-title
{	padding: 5px 20% 0px 0px !important;
	text-align: center !important;
}
.forum-block-container
{	background-color: #003E63 !important;
	background: rgb(0,62,99) !important;
	background: rgba(0,62,99) !important;
	border: 2px solid #006699 !important;
	border-top: none !important;
}
.forum-forum-list
{	margin-bottom: 0em !important;
}
th.forum-column-title
{	background-color: #001C31 !important;
	border-left: none !important;
	border-right: 1px solid #003E63 !important;
	color: #FFA34F !important;
	font-size: 11px !important;
}
.forum-head-title span a
{	font-weight: bold !important;
	font-size: 12px !important;
}
th.forum-column-topics, th.forum-column-replies, th.forum-column-views, th.forum-column-lastpost
{	background-color: #001C31 !important;
	border-left: none !important;
	border-right: 1px solid #003E63 !important;
	color: #FFA34F !important;
	font-size: 11px !important;
	text-align: center !important;
}
th.forum-column-lastpost
{	border-right: none !important;
}
.forum-row-first td, .forum-row-last td, .forum-row-odd td, .forum-row-even td
{	background-color: #003E63 !important;
	border: none !important;
	border-bottom: 1px solid #001C31 !important;
}
.forum-icon
{	height: 16px !important;
	width: 16px !important;
}
.forum-icon-newposts {
background-image: url('data:image/gif;base64,') !important;
}
.forum-icon-default {
background-image: url("data:image/gif;base64,") !important;
}
.forum-icon-sticky-newposts {
background-image: url("data:image/gif;base64,R0lGODlhEAAQAOMDAAAAAP8AALV6DPSkEP+rEbW1tefv9/f39/SkEPSkEPSkEPSkEPSkEPSkEPSkEPSkECH5BAEKAAgALAAAAAAQABAAAARQEMlJq734gk2F94AEBGSICAdBDIOwkTDwze4IB7KqE6MdCysWy+YLAYXD2MYYHBaKrlMTUKguDdGj82kYdLPTTbfbYgrHXmGWxgaJlvB4KAIAOw==")!important;
}
.forum-icon-sticky {
background-image: url("data:image/gif;base64,R0lGODlhEAAQALMAAAAAAGuUtYytxqW11q3G3rW1tdbe7+fv9/f39/8AAP/Ozv///////////////////yH5BAEAAAoALAAAAAAQABAAQARZUMlJqwqGaA2SByAgBYiREQHorQC2hccQD4JoVeFo7kbXrYnWiQAoGGG1i2DJbDJtt+goQE1JfkGls4b9uDbED+syJBawgOTXfI7F1IYBQUaf0WyhvB6kiAAAOw==")!important;
}
.forum-icon-closed, div.forum-icon-sticky-closed {
background-image: url("data:image/gif;base64,") !important;
}
td.forum-column-title
{	width: 70% !important;
	border-left: 1px solid #001C31 !important;
	border-right: 1px solid #001C31 !important;
}
td.forum-column-topics, td.forum-column-replies, td.forum-column-views
{	border-right: 1px solid #001C31 !important;
	font-size: 10px !important;
}
.forum-item-name a
{	font-weight: bold !important;
}
span.forum-item-desc
{	font-size: 11px !important;
}
a span.forum-lastpost-author
{	color: #ECECEC !important;
	font-size: 10px !important;
}
a:hover span.forum-lastpost-author
{	color: #FFA34F !important;
}
span.forum-lastpost-date
{	font-size: 10px !important;
}
td.forum-column-footer
{	background-color: #001C31 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 5px 0px 5px 0px !important;
	border: none !important;
	font-size: 11px !important;
}
/*ниже блок-контейнера*/
.forum-users-online
{	background-color: #003E63 !important;
	margin: 15px 0px 0px 0px !important;
	padding: 5px 1% 5px 1% !important;
	border: 2px solid #006699 !important;
	border-bottom: none !important;
	font-size: 10px !important;
	line-height: 150% !important;
}
.forum-users-online span
{	border: none !important;
}
.forum-users-online span a
{	text-decoration: underline !important;
}
.forum-statistics
{	background-color: #003E63 !important;
	margin: 0px 0px 15px 0px !important;
	padding: 5px 1% 5px 1% !important;
	border: 2px solid #006699 !important;
	border-top: 1px solid #001C31 !important;
	font-size: 11px !important;
}
.forum-main-footer
{	background-color: #001C31 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 10px 0% 0px 0% !important;
	border: none !important;
}
.forum-legend-info
{	font-size: 11px !important;
}
/*** Раздел форума **********************************************************************/
div.forum-navigation-box
{	background: #001C31 !important;
	border: none !important;
}
.forum-page-navigation
{	float: right !important;
	font-size : 11px !important;
	font-weight: bold !important;
}
.forum-page-navigation a, span.forum-page-current, span.forum-page-dots
{	padding: 0px 0px 0px 3px !important;
	border-left: none !important;
	border-right: 1px dashed #777777 !important;
}
.forum-page-navigation a
{	text-decoration: underline !important;
}
.forum-page-navigation a:hover, span.forum-page-current, span.forum-page-dots
{	text-decoration: none !important;
}
.forum-header-options
{	padding: 3px 2.5% 0px 2.5% !important;
	font-weight: normal !important;
	font-size: 11px !important;
}
span.forum-item-author, span.forum-lastpost-date a, span.forum-lastpost-author
{	font-size: 11px !important;
}
/*** Раздел форума в авторизации ********************************************************/
.forum-new-post
{	background-color: #777777 !important;
	margin: 3px 2.5% 0px 0px !important;
	padding: 0px 2.5% 1px 2.5% !important;
}
.forum-subscribes
{	background-color: #003E63 !important;
	border: 2px solid #006699 !important;
	border-top: none !important;
}

/*** Тема форума ************************************************************************/
.forum-post-first, .forum-post-odd, .forum-post-last, .forum-post-even 
{	border-top: none !important; 
	border-bottom: 1px solid #001C31 !important;
}
.forum-post-table
{	margin: 0px 0px 0px 0px !important;
}
td.forum-cell-user
{	background-color: #003E63 !important;
	width: 170px !important;
	border-left: none !important;
	border-right: 1px solid #001C31 !important;
	color: #BCBCBC !important;
	line-height: 100% !important;
}
.forum-user-name a, .forum-user-name span
{	font-size: 12px !important;
}
.forum-user-additional, .forum-user-status span
{	font-weight : normal !important;
	font-size: 10px !important;
}
.forum-user-additional, .forum-user-status
{	display: inline !important;
}
td.forum-cell-post
{	background-color: #003E63 !important;
	border: none !important;
}
.forum-post-date
{	background-color: #003E63 !important;
	border: none !important;
	color: #BCBCBC !important;
	font-size: 10px !important;
}
.forum-post-text
{	line-height: 150% !important;
}
.forum-post-text a:visited
{	color: #808000 !important;
}
.forum-post-text a:visited:hover
{	color: #CC701C !important;
}
table.forum-quote, table.forum-spoiler, table.forum-code, div.forum-code-box
{	background-color: #072D48 !important;
	margin: 0px 0.5% 0px 2.5% !important;
	padding: 0px 0.5% 0px 0.5% !important;
	border: 1px solid #003E63 !important;
	width: 95% !important;
}
table.forum-quote th, table.forum-spoiler th, table.forum-code th
{	background-color: #072D48 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	color: #777777 !important;
	font-size: 10px !important;
	font-weight : normal !important;
	line-height: 100% !important;
}
table.forum-quote td, table.forum-spoiler td, table.forum-code td, div.forum-code-box ol
{	background-color: #072D48 !important;
	color:#BCBCBC !important; 
 	font-size: 12px !important;
	line-height: 150% !important;
}
.forum-user-signature span
{	padding: 0px 0px 0px 0px !important;
 	font-size: 10px !important;
}
.forum-signature-line
{	border-top-color: #777777 !important;
}
td.forum-cell-contact
{	height: 0px !important;
	border-top: none !important;
	border-left: none !important; 
	border-right: 1px solid #001C31 !important;
 	font-size: 11px !important;
}
td.forum-cell-actions
{	height: 0px !important;
	border: none !important;
 	font-size: 11px !important;
	text-align : right !important;
}
.forum-contact-links, .forum-action-links
{	display: inline !important;
}
.forum-post-lastedit
{	font-size: 10px !important;
}
span.forum-post-lastedit-user a, span.forum-post-lastedit-user a:hover
{	color: #003E63 !important;
}
/*** Тема форума в авторизации **********************************************************/
.forum-help, .forum-reply-form 
{	background-color: #003E63 !important; 
	border: 2px solid #006699 !important; 
	border-top: none !important;
}
.forum-reply-header, .forum-reply-fields
{	background-color: #003E63 !important; 
	color: #FFA34F !important;
	font-size: 11px !important;
	font-weight: normal !important;
}
.bxlhe-frame-table, .lhe-stat-toolbar-cont, .bxlhe-editor-cell, .feed-add-post-form-but-wrap
{	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	border: none !important;
}
.bxlhe-editor-buttons, .feed-add-post-form-but-wrap
{	background: #AAAAAA !important;
 	height: 22px !important;
}
.bxlhe-frame
{	color: #BCBCBC !important;
	font-size: 12px !important;
	font-family: "Verdana", "Arial", "Helvetica", sans-serif !important;
	line-height: 150% !important;
}
.bxlhe-editor-cell
{	background-color: #000000 !important;
}
.feed-add-post-form-but
{	opacity: 1 !important;
}
textarea.lha-textarea
{	background-color: #000000 !important;
	color: #BCBCBC !important;
	font-size: 12px !important;
	font-family: "Verdana", "Arial", "Helvetica", sans-serif !important;
	line-height: 150% !important;
}
blockquote.bx-quote
{	background: #072D48 none !important;
	margin: 5px 0.5% 5px 2.5% !important;
	padding: 5px 0.5% 5px 0.5% !important;
	border: 1px solid #003E63 !important;
	color:#BCBCBC !important;
	font-size: 12px !important;
	font-family: "Verdana", "Arial", "Helvetica", sans-serif !important;
	line-height: 150% !important;
}
.forum-post-preview
{	background-color: #003E63 !important;
	border: 2px solid #006699 !important; 
	border-top: 1px solid #001C31 !important;
}
.forum-note-box, .forum-note-success
{	background-color: #001C31 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	border: none !important;
}

/*** Меню форума ************************************************************************/

/*** сначала "убить" три вредных тега ***/
th.forum-first-column, th.forum-last-column
{	border-left: none !important;
	border-right: none !important;
}
td.forum-last-column
{	border-left: 1px solid #001C31 !important;
	border-right: none !important;
}

/*** Обновления ****************************************************************************************************************/
.forum-filter
{	background: #001C31 !important;
	margin: 0px 0px 0px 0px !important;
	border: none !important;
}

/*** Профиль *******************************************************************************************************************/
.forum-tabs 
{	background-color: #003E63 !important;
	height: 350px !important;
	padding: 20px 1% 20px 1% !important;
	border: 2px solid #006699 !important;
}
.tab-container, .tab-container div, .tab-container-selected, .tab-container-selected div
{	padding: 0px 0px 10px 20px !important;
	border: none !important;
	color: #ECECEC !important;
	font: bold 12px "Verdana", "Arial", "Helvetica", sans-serif !important; 
}
.tab-container-selected div
{	color: #FFA34F !important;
}
.forum-user-hide-action
{	padding: 3px 2.5% 0px 2.5% !important;
	font-size: 11px !important; 
}
.forum-user-private-info th.forum-last-column
{	background-color: #003E63 !important;
	border-left: none !important;
	border-right: none !important;
	border-bottom: 1px solid #001C31 !important;
