@-moz-document domain("urbandictionary.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#slogan-hidden,

.panel h4,
.slogan-container,
.slogan-panel .like_buttons a,
.uppercase
{
	text-transform: capitalize!important;
}

.def-panel .thumbs .up,
.def-panel .thumbs .down,
.filter-panel ul li,
.panel,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.def-footer .circle-link .svgicon svg,
.def-footer .thumbs .svgicon svg {
	fill: #e4c7c7!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

selector
{
	font-size: 2.5rem!important;
}

.collection-panel .title .highlight,

body
{
	font-size: 1.5rem!important;
}

selector
{
	font-size: 1.2rem!important;
}

xxx
{
	line-height: 2.5rem!important;
}

.p-5 .break-words
{
	line-height: 2.1rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.ad-panel,
.flex.items-center.justify-center.mb-4.overflow-hidden,
.items-center.justify-center.overflow-hidden,

[class*="-ad"]:not(.svgicon-add202)
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.large-4,
.small-12 {
	top: 2rem;
}

.search-and-actions .main-actions a {
	margin-bottom: -0.65em!important;
}

.def-panel .example {
	margin: 1em 0 1em 2rem!important;
}

.columns.search-and-actions {
	top: 0rem;
	width: 90%!important;
}

#outer {
	margin-top: 0!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

.large-4,
.large-8,
.small-12
{
	margin: 1.2rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.def-panel,
.large-4,
.large-8,
.slogan-panel,
.small-12
{
	padding: 1.2rem!important;
}

.category.sex {
	padding: 6px!important;
}

x.text-light-charcoal {
	padding: .5rem 1.85rem!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.qtip.qtip-urban .qtip-content {
	Max-height: none!important;
}

.large-8 {
    height: auto;
}

.slogan-panel .slogan {
    min-height: 126px;
}

/* Widths 
_____________________________________________________________________________________________________*/

.container,
.row {
	max-width: none!important;
}

.large-8 {
	width: 63.66667%!important;
}

body {
	width: 98%!important;
}



}

