.answer_container {

  background-color: #ffff66 !important;

}

body {

background: #A51D1F url('http://i.imgur.com/MAHqK.png') !important;
background-position: right bottom !important;
background-attachment:fixed !important;
background-repeat: yes-repeat !important;

}

#posts .notification a:not(.avatar_frame) {

color: #a1000 !important;

font-weight: bold !important;

text-decoration: none !important;

border-bottom: 0px solid #a10000;

-webkit-background-clip: padding-box;

}

#posts .notification blockquote {

  border-left: solid 3px #a10000 !important;

}

#posts .notification .avatar_frame {

background-color: #a10000 !important;

border: 1px solid #a10000 !important;

position: absolute;

top: -1px;

left: -55px;

overflow: hidden;

display: inline-block;

}

.post.is_mine.with_avatar.new_post {

  color: #a10000 !important;
 
  opacity: 1

  font-family:courier, monospace !important;

  background: #ffffff !important; 

}

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{

opacity:0.00 !important;  

}

label {

-webkit-font-smoothing: antialiased;

opacity: 1;

font-size: 14px;

font-weight: normal;

color: #ffffff;

text-decoration: none;

text-transform: uppercase;

text-align: center;

width: 74px;

position: absolute;

}

#content {

  background: #a10000 !important; 

  opacity: 1.5

}

.selection_nipple:not([class="selection_nipple white"]) {

border-bottom: solid 11px #c80000  !important;

}

#content_top {

  width: 0px !important;

  height: 0px !important;

}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

background: none !important;

width: 0px !important;

height: 0px !important;

margin-top: 0px !important;

border-bottom: solid 11px #a10000 !important;

border-right: 11px solid transparent !important;

border-left: 11px solid transparent !important;

}

#content_bottom {

  width: 0px !important;

  height: 0px !important;
}

#content {

  padding-top: 25px !important; 

  padding-bottom: 25px !important; 

   -moz-border-radius: 20px !important; 

}

LABEL {

  color: rgba #a10000 !important; 

}

#footer {

  display: none !important; 

}

.radar {

  display: none !important; 

}


.selected .following,

.selected .followers,

.selected .settings,

.selected .posts,

.selected .messages,

.selected .drafts,

.selected .queue,

.selected .likes  {

  color: rgba(0, 0, 0, 0.50) !important; 

  background: #ff3f3f !important; 

}

#new_post .arrow {

  opacity: 0.50 !important;

  margin-left: -1px !important; 

}
