	border: 2px solid #AAAAAA !important; 
	color: #BCBCBC !important;
}
/*** Центральный блок **********************************************************************************************************/
#center
{ 	background: #001C31 none !important;
	left: 0 !important;
	margin: 0px 1px 0px 1px !important;
	padding: 10px 0px 10px 0px !important;
	width: 99.6% !important;
	border: 1px solid #BCBCBC !important;
}
/*** левая колонка ***************************/
#leftcol
{	width: 22% !important;
	min-width: 150px !important;
	margin: 0px 0.5% 0px 0% !important;
	padding: 0px 0.5% 0px 1.5% !important;
	border-right: 1px solid #BCBCBC !important;
}
/*** заголовок "Новый номер" ***/
#leftcol div h3
{	color: #FFA34F !important;
	font-weight: bold !important;
}
/*** ссылки: заголовок "Новости", текст новости ***/
#leftcol div.left_news h3 a,
#leftcol div.left_news p.news-item a
{	color: #ECECEC !important;
}
/*** эти ссылки с наведением ***/
#leftcol div.left_news h3 a:hover,
#leftcol div.left_news p.news-item a:hover
{	color: #FFA34F !important;
}
/*** дата новости ***/
#leftcol div.left_news p.news-item span.news-date-time
{	color: #BCBCBC !important;
}
/*** правая колонка **************************/
#wa
{	width: 66% !important;
	padding: 0px 0px 50px 4% !important;
	border-left: 1px solid #BCBCBC !important;
}
#wa h1
{	color: #FFA34F !important;
}
/*** Блок время, фото, ФИО, должность ***/
#wa div.card
{	width: 33% !important;
	min-width: 120px !important;
	margin: 0px 0% 20px 0% !important;
	font-family: "Arial","Helvetica",sans-serif !important;
}
#wa div.card div
{	border: none !important;
}
/*** абзац ФИО ***/
#wa div.card p.name
{	width: 120px !important;
	color: #DCDCDC !important;
	font-weight: bold !important;
}
/*** должность ***/
#wa div.card p.name span
{	color: #FFA34F !important;
	font-weight: normal !important;
}
/*** Нижний блок ***************************************************************************************************************/
#footer1,
#footer2
{ 	background: #001C31 none !important;
	left: 0% !important;
	width: 100% !important;
	height: 0px !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
}
#footer2
{	height: 80px !important;	
}
#botmenustr
{	width: 90% !important;
	margin: 0px 0% 0px 0% !important;
	padding: 25px 0% 0px 2% !important;
}
#botmenustr a
{	padding:  0px 1% 0px 1% !important;
	font-size: 11px !important;
}
#botmenustr a.selected
{	color: Gray !important;
	text-decoration: underline !important;
}
#copyright
{	margin: 0px 0px 0px 0px !important;
	padding: 25px 0px 0px 1% !important;
}
#counters
{	margin: 0px 0px 0px 0px !important;
	padding: 0px 1% 5px 0% !important;
}
#bottomline
