#leftcol div.news-filter form
{	width: 145px !important;
}
/*** правая колонка **************************/
#wa div.news-list
{	background-color: #003E63 !important;
	border: 2px solid #006699 !important;
	width: 85% !important;
}
#wa div.news-list p.news-item
{	margin: 0px 0px 0px 0px !important;
	padding: 5px 1.5% 0px 1.5% !important;
	border-bottom: 1px solid #001C31 !important;
	line-height: 150% !important;
}
#wa div.news-list p.news-item span.news-date-time
{	color: #FFA34F !important;
}
