}
div.forum-item-name span.forum-item-title a
{	font-weight: bold !important;
	font-size: 12px !important;
}
div.forum-lastpost-box span.forum-lastpost-title a
{	font-size: 12px !important;
}
div.forum-lastpost-box span.forum-lastpost-title a span.forum-lastpost-author
{	font-size: 10px !important;
}
td.forum-column-footer
{	background: #001C31 none !important;
	margin: 0px 0px 0px 0px !important;
	padding: 5px 0px 5px 0px !important;
	border: none !important;
	font-size: 11px !important;
}
/*ниже блок-контейнера*/
div.forum-users-online,
div.forum-users-birthday,
div.forum-statistics
{	background-color: #003E63 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 5px 1% 5px 1% !important;
	border: 2px solid #006699 !important;
	border-bottom: 1px solid #001C31 !important;
	font-size: 10px !important;
}
div.forum-users-birthday
{	border-top: none !important;
}
div.forum-statistics
{	border-top: none !important;
	border-bottom: 2px solid #006699 !important;
}
span.forum-user-online a,
span.forum-users-birthday a
{	font-weight: bold !important;
	text-decoration: underline !important;
}
span.forum-user-online a:hover
{	text-decoration: none !important;
}
div.forum-main-footer
{	background-color: #001C31 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 25px 0% 0px 0% !important;
	border: none !important;
}
div.forum-legend-info
{	font-size: 11px !important;
}
