	border-left: 1px solid #EEEEEE !important;
	border-right: 1px solid #777777 !important;
	border-bottom: 1px solid #777777 !important;
	font-size: 11px !important;
	color: #000000 !important;
}
div.forum-header-options span a:hover,
div.forum-navigation-box div.forum-new-post a:hover
{	background-color: #B0B0B0 !important;
	padding: 0px  2px  2px  3px  !important;
	border-top: 1px solid #777777 !important;
	border-left: 1px solid #777777 !important;
	border-right: 1px solid #EEEEEE !important;
	border-bottom: 1px solid #EEEEEE !important;
	text-decoration: none !important;
}
div.forum-lastpost-box span.forum-lastpost-date a,
div.forum-lastpost-box span.forum-lastpost-title span.forum-lastpost-author
{	font-size: 11px !important;
}
