	border: none !important;
}
div.forum-post-date
{	background-color: #003E63 !important;
	padding: 0px 0.5% 0px 0.5% !important;
	border: none !important;
	color: #BCBCBC !important;
	font-size: 10px !important;
}
div.forum-post-entry
{	padding: 0px 0.5% 0px 0.5% !important;
 	font-size: 12px !important;
}
div.forum-post-entry div.forum-post-text a:visited
{	color: #808000 !important;
}
div.forum-post-entry div.forum-post-text a:visited:hover
{	color: #CC701C !important;
}
/*** блок цитат, спойлера, кода ***/
table.forum-quote,
table.forum-spoiler,
table.forum-code,
div.forum-code-box
{	width: 95% !important;
	margin: 0px 0.5% 0px 2.5% !important;
	padding: 0px 0.5% 0px 0.5% !important;
	border: 1px solid #003E63 !important;
 	font-size: 12px !important;
}
/*** Поле со словом "цитата", спойлер, код ***/
table.forum-quote th,
table.forum-spoiler th,
table.forum-code th
{	background-color: #072D48 !important;
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 0px 0px !important;
	color:#777777 !important; 
	font-size: 10px !important;
	font-weight : normal !important;
	line-height: 100% !important;
}
/*** поле с содержанием цитаты, спойлера, кода ***/
table.forum-quote td, table.forum-spoiler td, table.forum-code td, div.forum-code-box ol
