css:
@-moz-document domain('furaffinity.net') {
div #fa_header {
	opacity: 0.7;
}
A:LINK{
	color: #ddd !important;
}
A:VISITED{
	color: #ddd !important;
}
body{
	background-color: #000 !important;
}

    body>div, .maintable, .alt1, .cat, .lead, .alt2 {
        background-color: #000000 !important;
        color: #808080 !important;
    }
	
	#page-galleryscraps .page-name {
    display: table;
    width: 100%;
    background-color: #121212;
}
	#page-galleryscraps .pagination {
    margin: 20px auto 10px;
    text-align: center;
		color: #999;
    background-color: #333;
    width: 340px;
    line-height: 20px;
    border-radius: 12px;
}
.button, .button-link {
    color: #333 !important;
    background-color: #555;
    border: 1px solid #CCCACB;
}
	
.links{
	border-top: 2px solid #999 !important;
	border-bottom: 2px solid #999 !important;
	color: #ddd !important;
	background-color: #282828;
}
.links A:LINK{
	color: #ddd !important;
}
.links A:VISITED{
	color: #ddd !important;
}
.links A:HOVER{
	color: #ddd !important;
}
.footer{
	color: #999 !important;
}
.footer A:LINK{
	color: #bbb !important;
}
.footer A:VISITED{
	color: #bbb !important;
}
.maintable{
	background-color: #121212 !important;  /*#919bad*/
}
.alt1, .alt1 A:LINK, .alt1 A:VISITED{
	background-color: #000 !important;
	color: #aaa !important;
}


.lead{
	border-top: 2px solid #888 !important;
	border-bottom: 2px solid #888 !important;
}
.cat{
	background-color: #282828 !important;
	color: #ddd !important;
	border: 2px solid #999 !important;
}
.alt2{
	background-color: #111 !important;
	color: #999 !important;
}
.alt2 A:LINK{
	color: #ddd !important;
}
.alt2 A:VISITED{
	color: #ddd !important;
}
.button{
	color: #999 !important;
	background-color: #000000 !important;
	border: 1px solid #ccc !important;
}
.fieldset{
	border: 1px ridge #ddd !important;
}
.hovertable{
	background-color: #ddd !important;
}
.hovertable_head{
	background-color: #ddd !important;
	color: #fff;
	border: 2px solid #d4dce8;
}
.hovertable_body{
	background-color: #444 !important;
	color: #4b4b4b;
}
.thumbnail{
	opacity: 0.7 !important;
}



.innertable{
	background-color: #000 !important;
}
.logo{
	background-color: #111 !important; /*#d4dce8;*/
}
.tab u{
	background-color: #666 !important;
	border-top: 1px solid #444 !important;
	border-left: 1px solid #444 !important;
	border-right: 1px solid #444 !important;
	color: #ddd !important;
}
.tab a{
	background-color: #444 !important;
	border-top: 1px solid #444 !important;
	border-left: 1px solid #444 !important;
	border-right: 1px solid #444 !important;
}
.ldot{
	border-left: 1px dotted #999 !important;
}
.rdot{
	border-right: 1px dotted #999 !important;
}
.bdot{
	border-bottom: 1px dotted #999 !important;
}
.tdot{
	border-top: 1px dotted #999 !important;
}
.menu li ul li{
	border: 1px dotted #ddd !important;
	background-color: #555 !important;
}
.menu li ul li:hover{
	border: 1px dotted #555 !important;
	background-color: #ddd !important;
	color: #282828 !important;
}
.menu li ul li A:LINK{
	color: #282828 !important;
}
.menu li ul li A:VISITED{
	color: #282828 !important;
}

.container-comment{
	border: 1px solid #555 !important;
}
.container-comment TD,
.container-comment TH{
	color: #999 !important;
}
.container-comment TH{
	background-color: #555 !important;
}
.container-comment TD.cat,
.container-comment TH.lead{
	border: 0px none !important;
}
.container-comment TH.lead H4{
	color: #bbb !important;
}
.container-comment TD.cat A{
	background-color: #444 !important;
}
.container-comment TD.cat{
	border: 0 !important;
	background-color: #000 !important;
}
div#messagecenter-submissions{
	color: #999 !important;
}
div#messagecenter-submissions div.navigation a{
	color: #DDD !important;
	background-color: #222 !important;
	border: 1px solid #555 !important;
}
div#messagecenter-submissions div.actions .remove-checked{
	background-color: #468 !important;
	color: #AAA !important;
}
div#messagecenter-submissions div.actions .remove-nuke{
	background-color: #C81010 !important;
	color: #000 !important;
	border: 1px solid #666 !important;
}
div#messagecenter-submissions table.checked{
	background-color: #555 !important;
}
div#messagecenter-submissions h3.date-divider{
	border-bottom: 1px solid #555 !important;
}
#description_popup{
	border: 1px solid #555 !important;
	background-color: #111 !important;
}
#description_popup h5{
	background-color: #444 !important;
	color:  #ddd !important;
}
#description_popup span{
	border-top: 1px solid #555 !important;
	color: #ddd !important;
}

.popup_date{
	border-bottom: 1px dotted #A2A3A3 !important;
}

div#messagecenter-other ul.message-stream li em {
border-bottom: 1px dotted #A2A3A3 !important;
}
div#messagecenter-other ul.message-stream li em{
	border-bottom: 1px dotted #a2a3a3 !important;
}

div#messagecenter-other fieldset h3 input{
	background-color: #B96F6D !important;
	color: #333 !important;
}

div#messagecenter-other {
                        color: #aaa !important;
}

div#messagecenter-other ul.message-stream table.checked{
	background-color: #363636 !important;
}
div#messagecenter-other ul.message-stream li.checked{
	background-color: #363636 !important;
}
div#messagecenter-other ul.message-stream li.hover{
	background-color: #6B6B6B !important;
}

div#messagecenter-other ul.message-stream li.section-controls,
div#messagecenter-other #messages-watches ul.message-stream li.section-controls{
	border-bottom: 1px solid #858587 !important;
}

.textarea, .textbox, .listbox, select, textarea, .uploadfield {
          color: #999 !important;
          background: #000 !important;
}

.no_overflow a:hover, .no_overflow a:link, .no_overflow a:visited, .cat a:hover, .cat a:link, .cat a:visited, .lead a:hover, .lead a:link, .lead a:visited{
background: rgba(0, 0, 0, 0.0) !important;
}
}
