css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url('https://www.tumblr.com/login'),
  url('http://www.tumblr.com/dashboard'),
  url('http://www.tumblr.com/new/blog'),
  url('http://www.tumblr.com/inbox'),
  url('http://www.tumblr.com/help'),
  url('https://www.tumblr.com/preferences'),
  url('http://www.tumblr.com/lookup'),
  url('http://www.tumblr.com/explore'),
  url('http://www.tumblr.com/following'),
  url('http://www.tumblr.com/likes'),
  url-prefix('http://www.tumblr.com/blog/'),
  url-prefix('http://www.tumblr.com/dashboard/'),
  url-prefix('http://www.tumblr.com/new/'),
  url-prefix('http://www.tumblr.com/tumblelog/'),
  url-prefix('http://www.tumblr.com/tagged/'),
  url-prefix('http://www.tumblr.com/spotlight/'),
  url-prefix('http://www.tumblr.com/reblog/'),
  url-prefix('http://www.tumblr.com/inbox/'),
  url-prefix('http://www.tumblr.com/edit/') , url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/dashboard/"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/inbox/"), url-prefix("https://www.tumblr.com/edit/") {

/*sets text color for ALL text*/
/*
* {
  color:rgba(0, 0, 0, 1.0) !important;
}
*/

.l-header-container--refresh {background: rgba(0,0,0,0)!important}

 #dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#dashboard_members div.l-content,#main_tag div.l-content,#main_tag_beta div.l-content, #dashboard_inbox div.l-content,#dashboard_tumblelog_settings div.l-content,#dashboard_followers div.l-content,#tagged_display #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(0,0,0,0.3) !important
} 

div#right_column,div#right_column:after{
    background: none !important;
    }

.left_column {background: none !important;}

#dashboard_index,#dashboard_edit_post,#dashboard_submissions,#dashboard_drafts,#dashboard_post_queue,#dashboard_likes,#dashboard_followers,#dashboard_following,#dashboard_members,#main_tag,#main_tag_beta,#dashboard_inbox,#dashboard_tumblelog_settings,#dashboard_directory,#tagged_display {
background:#000000 url(http://i167.photobucket.com/albums/u136/egmiscool/dark.jpg) repeat right bottom fixed !important
}

#left_column #posts .notification {
background-color:#ffffff !important;
border-bottom:none !important;
color:#aaa !important
}

#left_column #posts .notification.alt,#dashboard_switch_blog_menu_footer,#dashboard_switch_blog_menu_wrapper {
background-color:#ffffff !important
} 

#left_column #posts .notification .avatar {
background-color:#fff !important;
border-color:transparent !important;
opacity:0.8 !important
}

#left_column #posts .notification .avatar:hover {
opacity:1 !important
}

#left_column #posts .notification .block,.post_info,.post_info a {
color:#000000 !important
}

#left_column #posts .notification a {
border:none !important;
color:#000000 !important
}

#left_column #posts .notification blockquote {
border-left-color:#000000 !important
}

#left_column #posts .notification.alt,#dashboard_switch_blog_menu_footer,#dashboard_switch_blog_menu_wrapper {
background-color:#ffffff !important
}

#left_column #posts .post .post_avatar,#left_column #posts .post .sub_avatar .sub_avatar_inner {
background-color:#ffffff !important;
border-bottom:none !important
}

#left_column #posts .post,.dashboard_options_form {
border-bottom:none !important
}

#nav .nav_item .nav_menu a {
border-top-color:#ffffff !important;
color:#ffffff !important
}

#nav .nav_item .nav_menu a:hover,.no_posts_found,h1.dashboard_header,h1.dashboard_header a,h1.dashboard_header span,#posts .post .footer_links a:hover,#posts .post .post_controls a:hover,#right_column,#right_column a,.dashboard_nav_title,.dashboard_subpages,.dashboard_controls_suggested_blog_title,.dashboard_controls_suggested_blog_description {
color:#ffffff;
}

#nav .nav_item .nav_menu,#dashboard_switch_blog_menu_header {
background-color:#ffffff !important;
border:none !important
}

#nav .nav_item.active,#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel,#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
background:#ffffff !important
}


/*AVATAR ARROW begin - arrow to the left of the icon bar*/
#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}
/*AVATAR ARROW end*/

/*CONTENT begin - background for posts, icon bar, sidebar*/
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(0,0,0, 0.5) !important;
}
/*CONTENT*/ 

/*POST BACKGROUND begin*/
#posts LI[class*=" post"] {
  background: rgba(255, 255, 255, 1.0) !important;
}
/*POST BACKGROUND end*/

/*SELECTION ARROW begin - points to dashboard selection*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255,1.0) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(255,255,255, .65)  !important;
}
/*SELECTION ARROW end*/

/*GREEN BUTTONS begin - blue/green buttons from sidebar to transparent*/
li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}
/*GREEN BUTTONS end*/

/*RADAR begin - removes radar blue frame - softens appearance*/
.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}
/*RADAR end*/


.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
.post_avatar
{
background-color: rgba(54, 70, 93, 0) !important;
}


/*DO NOT DELETE THIS BRACKET*/}
