css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("emucr.com") {
#wrap2, #page, html, .comment-thread {
    background: #2C2C2C !important;
    color: #eee !important;
}
h2 span, .footer {
    background: #111 !important;
}
.postMain {
    background: #333 !important;
    border: 1px solid #666 !important;
    border-radius: 6px !important;
    color: #eee !important;
    padding-left: 4px !important;
    padding-right: 4px !important;
    width: 110% !important;
}
.clearfloat {
    width: 70% !important;
}
#sidebar-middle {
    width: 100% !important;
}
#sidebar-top, #sidebar-left, #sidebar-right, #sidebar-bottom {
    background: #333 !important;
    color: #eee !important;
}
.post-footer-left, .logoleft {
    display: none !important;
}
.postMain a {
    color: #aaa !important;
}
.center.small {
    padding-top: 4px !important;
}
#logo.left img[src*="http://3.bp.blogspot.com/_Y6S_jlqe0OQ/TJv0025CbfI/AAAAAAAAAy0/orKSymTi284/s400/emucrlogo.gif"] {
    height: 84px !important;
    padding-left: 210px !important;
    width: 0px !important;
    background-image: url("http://i.imgur.com/bb47E.png") !important;
}
}
@-moz-document domain("www.emucr.com") {
#pagetopLeft {
    display: none !important;
}
}
