css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com") {


body {
background-color:#FFFFFF !important;
background-image:url('http://24.media.tumblr.com/tumblr_mdy716pKuc1rl9xebo1_500.png') !important;
background-repeat:repeat !important;
background-attachment:fixed !important;
background-position:center !important;
}

a {
font-size: 18px;
}

#pageNav{
background-color: #32bac0;
opacity: 1 !important;
}

#jewelContainer{
background-color: #48cdf6;
}

#blueBar{
background-color: #f67148 !important;
}

#leftCol{
background-color: #f67148 !important;
}

#contentArea{
background-color: #32bac0 !important;
font-size: 22px !important;
}

#rightCol{
background-color: #ffffcc !important;
-webkit-transform: translateX(-15px);
}


#profile_stream_container, #left_column, #contentArea , #leftCol, #info_tab{
background-color:none !important;
border:#fff solid 2px;
background-image:url('http://i.imgur.com/4pBX2.png') !important;
}

#pageLogo a {
background-color: transparent !important;
}
    
#pageLogo a:hover {
background-color: #000 !important;
}


.jewelHighlight li a:hover {
background-color: #fff !important;
}

.fbNubButton {
background: -moz-linear-gradient(center top , #000, #111111) !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}
    
.fbNubButton:hover {
background: #7b7a7b !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}

.fbNubButton {
background: -moz-linear-gradient(center top , #000, #111111) !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}
    
.fbNubButton:hover {
background: #7b7a7b !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}

.item:hover {
background: ##f72076 !important;
color: #FFFFFF !important;
}

.selectedItem a {
background: #000 !important;
color: #FFFFFF !important;
}



a.jewelButton:hover {
background-color: #FFFFFF !important;
}  



}
