css:
@-moz-document url-prefix("http://mba.dijon.fr/") {
	@media screen and (max-width:800px) {
		body {
			font-size:1.05em ! important;
			background-color:#FFFFFF ! important;
			padding:0.5em ! important;
		}
		div,h1,h2,h3,h4,ul,li,p,a,span,nav,header,section {
			background:none repeat scroll 0% 0% transparent ! important;
			height:auto ! important;
			line-height:normal ! important;
			left:0px ! important;
			margin:auto 0px ! important;
			max-width:100% ! important;
			padding:0px ! important;
			position:relative ! important;
			top:0px ! important;
			width:auto ! important;
			color:#000000 ! important;
		}
		#middle .breadcrumb li {
			display:inline ! important;
		}
		#sidebar-first,#block-addthis-0 {
			display:none ! important;
		}
		#block-menu-menu-cibles li {
			display:block ! important;
			float:left !important
		}
		#block-menu-menu-cibles li a {
			font-size:.9em ! important;
			padding:0.6em ! important;
			margin:0.4em ! important;
			border:1px solid grey ! important;
			border-radius:1em ! important;
			background:linear-gradient(0deg,#E2E2E2,#DBDBDB 50%,#D1D1D1 51%,#FEFEFE 100%) repeat scroll 0% 0% transparent ! important;
		}
		a,span {
			background:none repeat scroll 0% 0% transparent ! important;
			width:auto ! important;
			padding:0px ! important;
			height:auto ! important;
			line-height:normal ! important;
		}
		table,td,blockquote,code,pre,textarea,input,iframe,object,embed,video {
			max-width:85% ! important;
		}
		h2,textarea,table,td,th,code,pre,samp {
			word-wrap:break-word;
		}
		code,pre,samp {
			white-space:pre-line;
		}
		#menu li {
			border:medium none ! important;
			float:left !important;
			clear:left !important;
			width:auto !important
		}
		#menu li a,#nav ul li a {
			font-size:1.1em ! important;
			padding:0.6em 0px 0.6em 1em ! important;
			margin:1em 0px 0px 1em ! important;
			width:90% ! important;
			border:1px solid grey ! important;
			border-radius:1em 1em 1em 1em ! important;
			background:linear-gradient(0deg,#E2E2E2,#DBDBDB 50%,#D1D1D1 51%,#FEFEFE 100%) repeat scroll 0% 0% transparent ! important;
		}
		#menu ul {
			min-height:auto ! important;
		}
		#menu li ul {
			display:block ! important;
			visibility:visible ! important;
			margin:0px 0px 0px 2em ! important;
		}
		#menu a span {
			color:black ! important;
		}
		p {
			margin:0px 0px 1em ! important;
		}
		#views_slideshow_singleframe_div_accueil_slider-block_1_1,#views_slideshow_singleframe_div_accueil_slider-block_1_2,#views_slideshow_singleframe_div_accueil_slider-block_1_3,#views_slideshow_singleframe_div_accueil_slider-block_1_4,#views_slideshow_singleframe_div_accueil_slider-block_1_5 #views_slideshow_singleframe_div_accueil_slider-block_1_5 {
			opacity:1 ! important;
			display:block ! important;
		}
		footer {
			margin-top: 2em !important
		}
	}
}
